import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Registration.css';

const Registration = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        username: '',
        password: '',
        confirmPassword: ''
        
    });

    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (formData.password !== formData.confirmPassword) {
            alert("Passwords do not match!");
            return;
        }
        try {
            const response = await axios.post('http://localhost:5000/api/register', formData);
            alert(response.data.message);
           navigate('/'); // Redirect to login page
        } catch (error) {
            alert("Error registering user");
        }
    };

    return (
        <div className="registration-container">
            <h2>Register</h2>
            <form onSubmit={handleSubmit} className="p-4 border rounded shadow">
    <h2 className="mb-4 text-center">Register</h2>
    
    <input type="text" className="form-control mb-3" name="name" placeholder="Name" value={formData.name} onChange={handleChange} required />
    <input type="email" className="form-control mb-3" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
    <input type="text" className="form-control mb-3" name="username" placeholder="Username" value={formData.username} onChange={handleChange} required />
    <input type="password" className="form-control mb-3" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required />
    <input type="password" className="form-control mb-3" name="confirmPassword" placeholder="Confirm Password" value={formData.confirmPassword} onChange={handleChange} required />
    
    <div className="mb-3">
        <label className="form-label">Role:</label>
        <div className="form-check">
            <input 
                type="radio" 
                className="form-check-input" 
                name="role" 
                value="faculty" 
                checked={formData.role === 'faculty'} 
                onChange={handleChange} 
                required 
            />
            <label className="form-check-label">Faculty</label>
        </div>
        <div className="form-check">
            <input 
                type="radio" 
                className="form-check-input" 
                name="role" 
                value="student" 
                checked={formData.role === 'student'} 
                onChange={handleChange} 
                required 
            />
            <label className="form-check-label">Student</label>
        </div>
    </div>
    
    <button type="submit" className="btn btn-primary btn-block">Register</button>
</form>
        </div>
    );
};

export default Registration;
