import React, { useState } from 'react';

const NptelContent = () => {
    const [forms, setForms] = useState([]);
    const [videoTitle, setVideoTitle] = useState('');
    const [videoDescription, setVideoDescription] = useState('');
  
    const handleAddForm = () => {
      setForms([...forms, { id: forms.length + 1, title: '', description: '' }]);
    };
  
    const handleRemoveForm = (id) => {
      setForms(forms.filter((form) => form.id !== id));
    };
  
    const handleVideoTitleChange = (id, event) => {
      const updatedForms = forms.map((form) => {
        if (form.id === id) {
          return { ...form, title: event.target.value };
        }
        return form;
      });
      setForms(updatedForms);
    };
  
    const handleVideoDescriptionChange = (id, event) => {
      const updatedForms = forms.map((form) => {
        if (form.id === id) {
          return { ...form, description: event.target.value };
        }
        return form;
      });
      setForms(updatedForms);
    };
  
    return (
      <div>
        <button onClick={handleAddForm}>Add Form</button>
        {forms.map((form) => (
          <div key={form.id}>
            <h2>Video Input Form {form.id}</h2>
            <form>
              <label>
                Video Title:
                <input
                  type="text"
                  value={form.title}
                  onChange={(event) => handleVideoTitleChange(form.id, event)}
                  placeholder="Enter video title"
                />
              </label>
              <br />
              <label>
                Video Description:
                <textarea
                  value={form.description}
                  onChange={(event) => handleVideoDescriptionChange(form.id, event)}
                  placeholder="Enter video description"
                />
              </label>
              <br />
              <button type="button" onClick={() => handleRemoveForm(form.id)}>
                Remove
              </button>
            </form>
          </div>
        ))}
      </div>
    );
  };
  
export default NptelContent;
