import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { useLocation } from "react-router-dom";

const TopicContent = () => {
  const location = useLocation();
  const { subject } = location.state || {};
  const [topics, setTopics] = useState([]);
  const [selectedTopic, setSelectedTopic] = useState("");
  const [subtopics, setSubtopics] = useState([]);
  const [selectedSubtopic, setSelectedSubtopic] = useState("");
  const [content, setContent] = useState("");
  const [loading, setLoading] = useState(false); // For loading state

  const navigate = useNavigate();

  // Fetch topics when the component mounts or `subject` changes
  useEffect(() => {
    const fetchTopics = async () => {
      setLoading(true);
      try {
        if (subject) {
          const response = await axios.get(
            `http://localhost:5000/api/topics?subjectId=${subject}`
          );
          setTopics(response.data);
        }
      } catch (error) {
        console.error("Error fetching topics:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchTopics();
  }, [subject]);

  // Fetch subtopics based on selected topic
  useEffect(() => {
    const fetchSubtopics = async () => {
      if (selectedTopic) {
        setLoading(true);
        try {
          const response = await axios.get(
            `http://localhost:5000/api/subtopics?topicId=${selectedTopic}`
          );
          setSubtopics(response.data);
        } catch (error) {
          console.error("Error fetching subtopics:", error);
        } finally {
          setLoading(false);
        }
      }
    };

    fetchSubtopics();
  }, [selectedTopic]);

  // Fetch content based on selected subtopic
  useEffect(() => {
    const fetchContent = async () => {
      if (selectedSubtopic) {
        setLoading(true);
        try {
          const response = await axios.get(
            `http://localhost:5000/api/content?subTopicId=${selectedSubtopic}`
          );
          setContent(response.data.content); // Assuming the response has a 'content' field
        } catch (error) {
          console.error("Error fetching content:", error);
        } finally {
          setLoading(false);
        }
      }
    };

    fetchContent();
  }, [selectedSubtopic]);

  return (
    <div className="container mt-4">
      <h2 className="mb-4">Topic Content</h2>

      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="row">
          {/* Left Column: Topics and Subtopics */}
          <div className="col-md-4">
            <h4>Select Topic</h4>
            <select
              className="form-select"
              value={selectedTopic}
              onChange={(e) => setSelectedTopic(e.target.value)}
            >
              <option value="">-- Select Topic --</option>
              {topics.map((topic) => (
                <option key={topic.id} value={topic.id}>
                  {topic.topic}
                </option>
              ))}
            </select>

            {selectedTopic && subtopics.length > 0 && (
              <>
                <h4>Select Subtopic</h4>
                <select
                  className="form-select"
                  value={selectedSubtopic}
                  onChange={(e) => setSelectedSubtopic(e.target.value)}
                >
                  <option value="">-- Select Subtopic --</option>
                  {subtopics.map((subtopic) => (
                    <option key={subtopic.id} value={subtopic.id}>
                      {subtopic.subTopic}
                    </option>
                  ))}
                </select>
              </>
            )}
          </div>

          {/* Right Column: Content */}
          <div className="col-md-8">
            <h4>Content</h4>
            {selectedSubtopic && content ? (
              <div>
                <p>{content}</p>
              </div>
            ) : (
              <p>Select a subtopic to view content.</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default TopicContent;
