import React, { useState } from 'react';
import axios from 'axios'; // Make sure to import axios

const Rating = ({ item }) => {
    console.log(item)
    const [feedbackMessage, setFeedbackMessage] = useState("");

    const handleRatingSubmit = async (e, itemId) => {
        e.preventDefault();
        const ratingValue = e.target.ratingValue.value;

        // API call to submit rating using Axios
        try {
            const response = await axios.post('http://localhost:5000/api/rating', {
                itemId,
                rating: ratingValue,
            });

            if (response.status === 200) {
                setFeedbackMessage(`Thank you for rating item ${itemId} as ${ratingValue} stars!`);
            }
            else if (response.status === 400) {
                setFeedbackMessage("You have already rated this item. Please try again later.");
            }
             else {
                setFeedbackMessage("Failed to submit rating. Please try again.");
            }
        } catch (error) {
            console.error("Error submitting rating:", error);
            setFeedbackMessage("An error occurred. Please try again later.",error);
        }

        e.target.reset(); // Reset the form after submission
    };

    return (
        <div>
            <p className="card-text">ID: {item}</p>

            {/* Inline Rating Form */}
            <form
                onSubmit={(e) => handleRatingSubmit(e, item)}
                className="d-flex align-items-center"
                style={{ gap: "0.5em" }}
            >
                <label htmlFor={`ratingValue-${item.id}`} className="mb-0">
                    Rate:
                </label>
                <input
                    type="number"
                    id={`ratingValue-${item}`}
                    name="ratingValue"
                    min="1"
                    max="5"
                    required
                    className="form-control"
                    style={{ width: "50px" }}
                />
                <button type="submit" className="btn btn-primary btn-sm">
                    Submit
                </button>
            </form>

            {/* Feedback Message */}
          ?  {feedbackMessage && <p>{feedbackMessage}</p>}
        </div>
    );
};

export default Rating;
