import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../../../Css/DropDown.css';

const DropDown = ({ onSubmit }) => {
  const [selectedTopic, setSelectedTopic] = useState('');
  const [selectedSubtopic, setSelectedSubtopic] = useState('');
  const [topics, setTopics] = useState([]);
  const [subtopics, setSubtopics] = useState([]);
  const [content, setContent] = useState('');

  // Fetch topics based on subject
  useEffect(() => {
    axios.get('http://localhost:5000/api/topics?subjectId=1') // Use actual subject ID
      .then((response) => {
        setTopics(response.data);
      })
      .catch((error) => {
        console.error('Error fetching topics:', error);
      });
  }, []); // Empty dependency array ensures this only runs once

  // Fetch subtopics when a topic is selected
  useEffect(() => {
    if (selectedTopic) {
      axios.get(`http://localhost:5000/api/subtopics?topicId=${selectedTopic}`)
        .then((response) => {
          setSubtopics(response.data);
          setContent(''); // Reset content when changing topic
        })
        .catch((error) => {
          console.error('Error fetching subtopics:', error);
        });
    }
  }, [selectedTopic]);

  // Fetch content when a subtopic is selected
  useEffect(() => {
    if (selectedSubtopic) {
      axios.get(`http://localhost:5000/api/content?subTopicId=${selectedSubtopic}`)
        .then((response) => {
          setContent(response.data.content); // Assuming the response has a 'content' field
        })
        .catch((error) => {
          console.error('Error fetching content:', error);
        });
    }
  }, [selectedSubtopic]);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (selectedTopic && selectedSubtopic) {
      // Send selected topic, subtopic, and content data to parent
      onSubmit({ topic: selectedTopic, subtopic: selectedSubtopic, content });
    } else {
      alert('Please complete all selections.');
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit} className='dropdown-container'>
        <select value={selectedTopic} onChange={(e) => setSelectedTopic(e.target.value)}>
          <option value="">Select Topic</option>
          {topics.map((topic) => (
            <option key={topic.id} value={topic.id}>
              {topic.topic}
            </option>
          ))}
        </select>

        {selectedTopic && (
          <select value={selectedSubtopic} onChange={(e) => setSelectedSubtopic(e.target.value)}>
            <option value="">Select Subtopic</option>
            {subtopics.map((subtopic) => (
              <option key={subtopic.id} value={subtopic.id}>
                {subtopic.subTopic}
              </option>
            ))}
          </select>
        )}

        {selectedSubtopic && content && (
          <div className="content-display">
            <h4>Content</h4>
            <p>{content}</p>
          </div>
        )}

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default DropDown;
