import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './Home';
import Login from './Registration/Login';
import Logout from './Registration/Logout';
import StudentIndex from './StudentModule/StudentIndex';
import FacultyIndex from './FacultyModule/FacultyIndex'; // Assuming you have a FacultyIndex component

const MainApp = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    setIsLoggedIn(true); // Update login status to true
  };

  const handleLogout = () => {
    setIsLoggedIn(false); // Update login status to false
  };

  return (
    <Router>
      <div>
        <nav style={{ display: 'flex', justifyContent: 'flex-end', padding: '10px' }}>
          {/* Display "Logout" only when logged in */}
          {isLoggedIn && (
            <a href="/logout" className="btn btn-link" onClick={handleLogout}>
              Logout
            </a>
          )}
        </nav>
        <Routes>
          <Route
            path="/"
            element={<Home isLoggedIn={isLoggedIn} onLogin={handleLogin} />} // Pass onLogin to Home
          />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/logout" element={<Logout onLogout={handleLogout} />} />
          <Route
            path="/studentindex/*"
            element={<StudentIndex isLoggedIn={isLoggedIn} />}
          />
          <Route
            path="/facultyindex/*"
            element={<FacultyIndex isLoggedIn={isLoggedIn} />}
          />
        </Routes>
      </div>
    </Router>
  );
};

export default MainApp;
