import React, { useEffect, useState } from 'react';
import '../../Css/Home.css'; // Import CSS for styling

const Home = () => {
    const [students, setStudents] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const hardcodedStudents = [
            {
                id: 1,
                name: 'Alice Johnson',
                age: 20,
                major: 'Computer Science',
                achievements: ['Dean\'s List', 'Hackathon Winner']
            }];
        const fetchStudents = async () => {
            try {
                const response = await fetch('https://api.example.com/students'); // Replace with your API endpoint
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setStudents(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchStudents();
    }, []);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div className="home-container">
            <header className="header">
                <h1>Welcome to the Student Information Portal</h1>
                <p>Empowering Students for Success</p>
            </header>
            <main className="student-profiles">
                {students.map(student => (
                    <div className="student-card" key={student.id}>
                        <h2>{student.name}</h2>
                        <p>Age: {student.age}</p>
                        <p>Major: {student.major}</p>
                        <p>Achievements: {student.achievements.join(', ')}</p>
                    </div>
                ))}
            </main>
            <footer className="footer">
                <p>Contact us: studentinfo@example.com | Phone: (123) 456-7890</p>
            </footer>
        </div>
    );
};

export default Home;
