import React, { useState, useEffect } from "react";
import axios from "axios";
import '../../../Css/NptelContent.css'; // Optional CSS for styling

const NptelContent = ({ subtopic }) => {
  const [isModalOpen, setIsModalOpen] = useState(false); // Modal state
  const [videos, setVideos] = useState([]); // List of videos
  const [error, setError] = useState(""); // Error state

  // Fetch videos when modal is opened
  const toggleModal = async () => {
    setIsModalOpen(!isModalOpen);

    if (!isModalOpen) {
      try {
        // Fetch videos for the given subTopic from the backend
        const response = await axios.get("http://localhost:5000/api/videos", {
          params: { subTopic: subtopic },
        });
        setVideos(response.data); // Set fetched videos in state
        setError(""); // Clear previous errors
      } catch (err) {
        setVideos([]); // Clear videos on error
        setError(err.response?.data?.error || "Failed to fetch videos");
      }
    }
  };

  return (
    <div className="nptel-container">
      <img
        src="/image/image.png"
        alt=""
        width={90}
        onClick={toggleModal} // Open the modal on click
        style={{ cursor: "pointer" }}
      />

      {isModalOpen && (
        <div className="nptel-modal-overlay">
          <div className="nptel-modal-content">
            <h2>Videos for Subtopic: {subtopic}</h2>

            {error && <p style={{ color: "red" }}>{error}</p>}

            <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
              {videos.length > 0 ? (
                videos.map((video, index) => (
                  <div key={index} style={{ width: "300px" }}>
                    <video
                      width="100%"
                      controls // Adds play/pause and other controls
                      style={{ border: "1px solid #ccc", borderRadius: "8px" }}
                    >
                      <source
                        src={`http://localhost:5000/videos/${encodeURIComponent(video.folderPath)}/${encodeURIComponent(video.videoName)}`}
                        type="video/mp4"
                      />
                      Your browser does not support the video tag.
                    </video>
                    <p style={{ textAlign: "center" }}>{video.videoName}</p>
                  </div>
                ))
              ) : (
                <p>No videos available for this subtopic.</p>
              )}
            </div>

            <button className="nptel-model-close" onClick={toggleModal}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default NptelContent;
