import React, { useState } from 'react';
import axios from 'axios';
import RatingModal from '../../../SubComponent/RatingModal'; // Import the modal component

const Rating = ({ itemId, username }) => {
    const [ratingValue, setRatingValue] = useState(0);
    const [feedbackMessage, setFeedbackMessage] = useState('');
    const [modalOpen, setModalOpen] = useState(false);

    const handleRatingSubmit = async () => {
        try {
            const response = await axios.post('http://localhost:5000/api/rating', {
                itemId,
                username,
                rating: ratingValue,
            });

            if (response.status === 200) {
                setFeedbackMessage(`Thank you for rating item ${itemId} as ${ratingValue} stars!`);
                setModalOpen(true); // Open the modal on successful submission
            } else {
                setFeedbackMessage("Failed to submit rating. Please try again.");
            }
        } catch (error) {
            console.error("Error submitting rating:", error);
            if (error.response && error.response.status === 400) {
                setFeedbackMessage('You have already rated this item');
                setModalOpen(true); // Open the modal if the item is already rated
            } else {
                setFeedbackMessage('An error occurred. Please try again.');
            }
        }
    };

    const handleCloseModal = () => {
        setModalOpen(false); // Close the modal
    };

    return (
        <div>
            {/* Your rating input UI here */}
            <button onClick={handleRatingSubmit}>Submit Rating</button>

            {/* Modal Component */}
            <RatingModal
                open={modalOpen}
                onClose={handleCloseModal}
                message={feedbackMessage}
            />
        </div>
    );
};

export default Rating;
