import React, { useState } from "react";
import axios from "axios";
import '../../../Css/NptelContent.css'; // Optional CSS for styling

const NptelContent = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  const [folderPath, setFolderPath] = useState("");
    const [videos, setVideos] = useState([]);
    const [error, setError] = useState("");

    const fetchVideos = async () => {
        try {
            const response = await axios.get("http://localhost:5000/api/videos", {
                params: { folderPath },
            });
            setVideos(response.data);
            setError(""); // Clear any previous errors
        } catch (err) {
            setVideos([]);
            setError(err.response?.data?.error || "Failed to fetch videos");
        }
    };



  return (
    <div className="nptel-container">
      <img
        src="/image/image.png"
        alt=""
        width={90}
        onClick={toggleModal}
        style={{ cursor: "pointer" }}
      />

      {isModalOpen && (
        <div className="nptel-modal-overlay">
          <div className="nptel-modal-content">
            <h2>Modal Title</h2>
            <p>This is the content of the modal.</p>
            <div style={{ padding: "20px" }}>
              <div style={{ marginBottom: "20px" }}>
                <input
                  type="text"
                  value={folderPath}
                  onChange={(e) => setFolderPath(e.target.value)}
                  placeholder="Enter folder path"
                  style={{ width: "300px", marginRight: "10px" }}
                />
                <button onClick={fetchVideos} className="btn btn-primary">
                  Fetch Videos
                </button>
              </div>

              {error && <p style={{ color: "red" }}>{error}</p>}

              <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
                {videos.map((video, index) => (
                  <div key={index} style={{ width: "300px" }}>
                    <video
                      width="100%"
                      controls
                      style={{ border: "1px solid #ccc", borderRadius: "8px" }}
                    >
                      <source src={`file://${video}`} type="video/mp4" />
                      Your browser does not support the video tag.
                    </video>
                    <p style={{ textAlign: "center" }}>{video.split("/").pop()}</p>
                  </div>
                ))}
              </div>
            </div>

            <button className="nptel-model-close" onClick={toggleModal}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default NptelContent;
