import React, { useState, useEffect } from 'react';
import '../../../Css/DropDown.css';
import axios from 'axios';
const DropDown = ({ onSubmit }) => {
    const [selectedCourse, setSelectedCourse] = useState('');
    const [selectedBranch, setSelectedBranch] = useState('');
    const [selectedSemester, setSelectedSemester] = useState('');
    const [selectedSubject, setSelectedSubject] = useState('');
    const [courses, setCourses] = useState([]);

    useEffect(() => {
        // Fetch the JSON data using axios
        axios.get('http://localhost:5000/api/courses')
            .then((response) => {
                console.log('Data fetched:', response.data);
                setCourses(response.data.courses);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    }, [selectedSubject]);

    const handleCourseChange = (e) => {
        setSelectedCourse(e.target.value);
        setSelectedBranch('');
        setSelectedSemester('');
        setSelectedSubject('');
    };

    const handleBranchChange = (e) => {
        setSelectedBranch(e.target.value);
        setSelectedSemester('');
        setSelectedSubject('');
    };

    const handleSemesterChange = (e) => {
        setSelectedSemester(e.target.value);
        setSelectedSubject('');
    };

    const handleSubjectChange = (e) => {
        setSelectedSubject(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Send selected subject data to parent
        onSubmit(selectedSubject);
        
    };

    const getBranches = () => {
        const course = courses.find((c) => c.name === selectedCourse);
        return course ? course.branches : [];
    };

    const getSemesters = () => {
        const branch = getBranches().find((b) => b.name === selectedBranch);
        return branch ? branch.semesters : [];
    };

    const getSubjects = () => {
        const semester = getSemesters().find((s) => s.name === selectedSemester);
        return semester ? semester.subjects : [];
    };

    return (
        <div >
        <form onSubmit={handleSubmit} className='dropdown-container'>
            <select value={selectedCourse} onChange={handleCourseChange}>
                <option value="">Select Course</option>
                {courses.map((course) => (
                    <option key={course.name} value={course.name}>
                        {course.name}
                    </option>
                ))}
            </select>

            {selectedCourse && (
                <select value={selectedBranch} onChange={handleBranchChange}>
                    <option value="">Select Branch</option>
                    {getBranches().map((branch) => (
                        <option key={branch.name} value={branch.name}>
                            {branch.name}
                        </option>
                    ))}
                </select>
            )}

            {selectedBranch && (
                <select value={selectedSemester} onChange={handleSemesterChange}>
                    <option value="">Select Semester</option>
                    {getSemesters().map((semester) => (
                        <option key={semester.name} value={semester.name}>
                            {semester.name}
                        </option>
                    ))}
                </select>
            )}

            {selectedSemester && (
                <select value={selectedSubject} onChange={handleSubjectChange}>
                    <option value="">Select Subject</option>
                    {getSubjects().map((subject) => (
                        <option key={subject} value={subject}>
                            {subject}
                        </option>
                    ))}
                </select>
            )}

            <button type="submit">Submit</button>
        </form>
        </div>
    );
};

export default DropDown;
