import React from "react";
import "./Table.css";
import "./functionality/ToolTip.css";
import ToolTip from "./functionality/ToolTip";

// ChapterContent Component for displaying content items
const ContentItem = () => {
  return (
    <div className="chapter-content">
      <h5>{title || "No Title Available"}</h5>
      <p>{description || "No Description Available"}</p>
      {link && (
        <a href={link} target="_blank" rel="noopener noreferrer">
          Read More
        </a>
      )}
      <p>Rating: {rating || "N/A"}</p>
    </div>
  );
};

const ChapterContent = ({ title, description, link, rating }) => {
  
  return (
    <div className="containers">
      <h2 className="subject-title">Subject: {selectedSubject}</h2>
      <div className="chapter">
        <div className="topic">
          <p>Topic: {selectedTopic}</p>
          <p>Subtopic: {selectedSubtopic}</p>
          <table className="content-table">
            <thead>
              <tr>
                <th>Basic Level</th>
                <th>Intermediate Level</th>
                <th>Advance Level</th>
                <th>Local NPTEL</th>
                <th>Live Video</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                {/* Basic Level */}
                <td>
                  {basicData.map((item, index) => (
                    <ContentItem
                      key={index}
                      title={item.title}
                      description={item.description}
                      link={item.link}
                      rating={item.rating}
                    />
                  ))}
                </td>

                {/* Intermediate Level */}
                <td>
                  {intermediateData.map((item, index) => (
                    <ContentItem
                      key={index}
                      title={item.title}
                      description={item.description}
                      link={item.link}
                      rating={item.rating}
                    />
                  ))}
                </td>

                {/* Advanced Level */}
                <td>
                  {advancedData.map((item, index) => (
                    <ContentItem
                      key={index}
                      title={item.title}
                      description={item.description}
                      link={item.link}
                      rating={item.rating}
                    />
                  ))}
                </td>

                {/* Local NPTEL */}
                <td>
                  {nptelData.map((item, index) => (
                    <ContentItem
                      key={index}
                      title={item.title}
                      description={item.description}
                      link={item.link}
                      rating={item.rating}
                    />
                  ))}
                </td>

                {/* Live Video */}
                <td>
                  {liveVideoData.map((item, index) => (
                    <ContentItem
                      key={index}
                      title={item.title}
                      description={item.description}
                      link={item.link}
                      rating={item.rating}
                    />
                  ))}
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default ChapterContent;
