import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Home from './Home';
import Login from './Registration/Login';
import StudentIndex from './StudentModule/StudentIndex';
import FacultyIndex from './FacultyModule/FacultyIndex'; // Assuming you have a FacultyIndex component

const MainApp = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  return (
    <Router>
      <div>
        <nav style={{ display: 'flex', justifyContent: 'flex-end', padding: '10px' }}>
          {!isLoggedIn && <Link to="/login">Login</Link>}
        </nav>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/student/*" element={<StudentIndex />} />
          <Route path="/facultyindex/*" element={<FacultyIndex />} />
        </Routes>
      </div>
    </Router>
  );
};

export default MainApp;
