import React, { useState, useEffect } from "react";
import axios from "axios";
import "../../../Css/NptelContent.css";

const NptelContent = ({ subtopic }) => {
  const [videos, setVideos] = useState([]);
  const [videoNames, setVideoNames] = useState([]);
  const [error, setError] = useState("");
  const [selectedVideo, setSelectedVideo] = useState(null);

  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/videos", {
          params: { subTopic: subtopic },
        });
        setVideos(response.data[0]);
        setVideoNames(response.data[1]);
        setError("");
      } catch (err) {
        setVideos([]);
        setError(err.response?.data?.error || "Failed to fetch videos.");
      }
    };

    fetchVideos();
  }, [subtopic]);

  const openVideo = (videoPath) => {
    setSelectedVideo(videoPath);
  };

  const closeModal = () => {
    setSelectedVideo(null);
  };

  return (
    <div className="nptel-container">
      <h2>Videos for Subtopic: {subtopic}</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}

      <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
        {videos.map((videoPath, index) => (
          <div
            key={index}
            style={{
              width: "300px",
              border: "1px solid #ccc",
              borderRadius: "8px",
              padding: "10px",
              cursor: "pointer",
              textAlign: "center",
            }}
            onClick={() => openVideo(videoPath)}
          >
            <img
              src="/image/nptelpng" // Replace with an actual thumbnail source
              alt={videoNames[index] || "Video"}
              style={{
                width: "100%",
                height: "auto",
                borderRadius: "4px",
                marginBottom: "10px",
              }}
            />
            <p>{videoNames[index] || "Untitled Video"}</p>
          </div>
        ))}
      </div>

      {selectedVideo && (
        <div className="nptel-modal-overlay">
          <div className="nptel-modal-content">
            <video
              width="700px"
              controls
              style={{ border: "1px solid #ccc", borderRadius: "8px" }}
            >
              <source
                src={`http://localhost:5000/api/video?path=${encodeURIComponent(
                  selectedVideo
                )}`}
                type="video/mp4"
              />
              Your browser does not support the video tag.
            </video>
            <button
              className="nptel-model-close"
              onClick={closeModal}
              style={{ marginTop: "10px" }}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default NptelContent;
