import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../../../Css/TwoColumnPage.css';

const TwoColumnPage = ({ selectedSubject }) => {
  const [topics, setTopics] = useState([]);
  const [subtopics, setSubtopics] = useState([]);
  const [selectedTopicId, setSelectedTopicId] = useState(null);
  const [selectedSubtopicId, setSelectedSubtopicId] = useState(null);
  const [content, setContent] = useState([]);

  // Fetch topics when the subject changes
  useEffect(() => {
    const fetchTopics = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/topics/${selectedSubject}`);
        setTopics(response.data.topics);
        // Reset state when subject changes
        setSubtopics([]);
        setSelectedTopicId(null);
        setSelectedSubtopicId(null);
        setContent([]);
      } catch (error) {
        console.error('Error fetching topics:', error);
      }
    };
    if (selectedSubject) fetchTopics();
  }, [selectedSubject]);

  // Fetch subtopics when a topic is selected
  const handleTopicSelect = async (topicId) => {
    setSelectedTopicId(topicId);
    setSelectedSubtopicId(null);
    setContent([]);

    try {
      const response = await axios.get(`http://localhost:5000/api/subtopics/${topicId}`);
      setSubtopics(response.data);
    } catch (error) {
      console.error('Error fetching subtopics:', error);
    }
  };

  // Fetch content when a subtopic is selected
  const handleSubtopicSelect = async (subtopicId) => {
    setSelectedSubtopicId(subtopicId);
    setContent([]);

    try {
      const response = await axios.get(`http://localhost:5000/api/content/${subtopicId}`);
      console.log('Content:', response.data);
      setContent(response.data);
    } catch (error) {
      console.error('Error fetching content:', error);
    }
  };
  const groupContentBySubTopicAndLevel = (content) => {
    const groupedContent = {};
  
    content.forEach((item) => {
      const { subTopicId, level, title, description, link, rating } = item;
  
      // Ensure subTopicId exists in the grouping
      if (!groupedContent[subTopicId]) {
        groupedContent[subTopicId] = {
          title: `SubTopic ${subTopicId}`, // Provide a generic title if none exists
          levels: {
            basic: [],
            medium: [],
            advanced: [],
          },
        };
      }
  
      // Push the content into the appropriate level
      groupedContent[subTopicId].levels[level]?.push({ title, description, link, rating });
    });
  
    return groupedContent;
  };
  
  const groupedContent = groupContentBySubTopicAndLevel(content);
  return (
    <div className="two-column-page">
      {/* Left Column: Topics and Subtopics */}
      <div className="left-column">
        <h3>Topics</h3>
        <ul>
          {topics.map((topic) => (
            <li key={topic.id}>
              <button
                onClick={() => handleTopicSelect(topic.id)}
                className={topic.id === selectedTopicId ? 'active' : ''}
              >
                {topic.topic}
              </button>
              {selectedTopicId === topic.id && (
                <ul className="subtopics-list">
                  {subtopics.map((subtopic) => (
                    <li key={subtopic.id}>
                      <button
                        onClick={() => handleSubtopicSelect(subtopic.id)}
                        className={subtopic.id === selectedSubtopicId ? 'active' : ''}
                      >
                        {subtopic.subTopic}
                      </button>
                    </li>
                  ))}
                </ul>
              )}
            </li>
          ))}
        </ul>
      </div>

       {/* Right Column: Grouped Content */}
       <div className="right-column">
      {selectedTopicId ? (
        <div>
          <h2>Content for Topic ID: {selectedTopicId}</h2>
          {content && Object.keys(content).length > 0 ? (
            Object.entries(content).map(([subtopicName, subtopicData]) => (
              <div key={subtopicName}>
                <h1>{subtopicData?.title || "Untitled Subtopic"}</h1>
                {["basic", "medium", "advanced"].map((level) => (
                  <div key={level} className="content-level">
                    <h4>{level.charAt(0).toUpperCase() + level.slice(1)} Level</h4>
                    <ul>
                      {subtopicData?.[level]?.length > 0 ? (
                        subtopicData[level].map((item, index) => (
                          <li key={index} className="content-item">
                            <h5>{item?.title || "No Title Available"}</h5>
                            <p>{item?.description || "No Description Available"}</p>
                            {item?.link && (
                              <a
                                href={item.link}
                                target="_blank"
                                rel="noopener noreferrer"
                              >
                                {item.link}
                              </a>
                            )}
                            <p>Rating: {item?.rating || "N/A"}/5</p>
                          </li>
                        ))
                      ) : (
                        <p>No content available for {level} level.</p>
                      )}
                    </ul>
                  </div>
                ))}
              </div>
            ))
          ) : (
            <p>Loading content or no content available for this topic.</p>
          )}
        </div>
      ) : (
        <p>Select a topic to view content</p>
      )}
    </div>
    </div>
  );
}

export default TwoColumnPage;
