import React,{useEffect} from 'react'; // Import React library
import '../../Css/Home.css'; // Import CSS for styling
import axios from 'axios';
const Home = () => {
    // Hardcoded student data

    const StudentComponent = () => {
        useEffect(() => {
            const fetchStudentDetails = async () => {
                try {
                    const response = await axios.get('https://localhost/student'); // Replace with your API endpoint
                    console.log(response.data); // Handle the response data
                } catch (error) {
                    console.error('Error fetching student details:', error);
                }
            };
    
            fetchStudentDetails();
        }, []);
    
    return (
        <div className="home-container">
            <header className="header">
                <h1>Welcome to the Student Information Portal</h1>
                <p>Empowering Students for Success</p>
            </header>
            <main className="student-profiles">
                {/* Map through the hardcoded student data to display each student's details */}
                {response.data.map(student => (
                    <div className="student-card" key={student.id}>
                        <h2>{student.name}</h2>
                        <p>Age: {student.age}</p>
                        <p>Major: {student.major}</p>
                        <p>Achievements: {student.achievements.join(', ')}</p>
                    </div>
                ))}
            </main>
        </div>
    );
};
};

export default Home; // Export the Home component
