import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../../../Css/TwoColumnPage.css';
import ChapterContent from './ChapterContent';

const TwoColumnPage = ({ selectedSubject }) => {
    const [topicsData, setTopicsData] = useState({});
    const [selectedTopic, setSelectedTopic] = useState('');
    const [selectedSubtopic, setSelectedSubtopic] = useState('');
    const [content, setContent] = useState('');

    // Fetch topics data when selectedSubject changes
    useEffect(() => {
        const fetchTopicsData = async () => {
            try {
                if (selectedSubject) {
                    const response = await axios.get(`http://localhost:5000/api/topics/${selectedSubject}`);
                    const data = response.data;
                    setTopicsData(data); // Assuming response contains topicsData structure
                    setSelectedTopic(''); // Reset the selected topic
                    setSelectedSubtopic(''); // Reset the selected subtopic
                    setContent(''); // Reset content
                }
            } catch (error) {
                console.error('Error fetching topics data:', error);
            }
        };

        fetchTopicsData();
    }, [selectedSubject]);

    // Handle topic selection
    const handleTopicSelect = (topic) => {
        setSelectedTopic(topic);
        setSelectedSubtopic('');
        setContent('');
    };

    // Handle subtopic selection
    const handleSubtopicSelect = (subtopic) => {
        setSelectedSubtopic(subtopic);
        setContent(topicsData[topic]?.[subtopic]); // Assuming topicsData has subtopic content
    };

    return (
        <div className="two-column-page">
            {/* Left column: Topics and Subtopics */}
            <div className="left-column">
                <h3>Chapter</h3>
                {Object.keys(topicsData).length > 0 ? (
                    <ul>
                        {Object.keys(topicsData).map((topic) => (
                            <li key={topic}>
                                <button
                                    onClick={() => handleTopicSelect(topic)}
                                    className={topic === selectedTopic ? 'active' : ''}
                                >
                                    {topic}
                                </button>
                                {topic === selectedTopic && topicsData[topic] && (
                                    <ul className="subtopics-list">
                                        {Object.keys(topicsData[topic]).map((subtopic) => (
                                            <li key={subtopic}>
                                                <button
                                                    onClick={() => handleSubtopicSelect(subtopic)}
                                                    className={subtopic === selectedSubtopic ? 'active' : ''}
                                                >
                                                    {subtopic}
                                                </button>
                                            </li>
                                        ))}
                                    </ul>
                                )}
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No topics available for the selected subject.</p>
                )}
            </div>

            {/* Right column: Content */}
            <div className="right-column">
                {selectedSubtopic ? (
                    <>
                        <ChapterContent 
                            selectedSubject={selectedSubject} 
                            selectedSubtopic={selectedSubtopic} 
                            content={content} 
                            className="chapter-content" 
                        />
                    </>
                ) : selectedTopic ? (
                    <p>Select a subtopic to view its content.</p>
                ) : (
                    <p>Select a topic to view its subtopics.</p>
                )}
            </div>
        </div>
    );
};

export default TwoColumnPage;
