import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../../../Css/TwoColumnPage.css';

const TwoColumnPage = ({ selectedSubject }) => {
  const [topics, setTopics] = useState([]);
  const [subtopics, setSubtopics] = useState({});
  const [selectedTopicId, setSelectedTopicId] = useState(null);
  const [selectedSubtopic, setSelectedSubtopic] = useState(null);
  const [content, setContent] = useState({ basic: [], medium: [], advanced: [] });

  // Fetch topics when the subject changes
  useEffect(() => {
    const fetchTopics = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/topics/${selectedSubject}`);
        setTopics(response.data.topics);
      } catch (error) {
        console.error('Error fetching topics:', error);
      }
    };

    if (selectedSubject) fetchTopics();
  }, [selectedSubject]);

  // Fetch subtopics when a topic is selected
  const handleTopicSelect = async (topicId) => {
    setSelectedTopicId(topicId);
    setSelectedSubtopic(null);
    setContent({ basic: [], medium: [], advanced: [] });

    try {
      const response = await axios.get(`http://localhost:5000/api/subtopics/${topicId}`);
      setSubtopics(response.data.subtopics); // Subtopics are now grouped by subtopic names
    } catch (error) {
      console.error('Error fetching subtopics:', error);
    }
  };

  // Handle subtopic selection
  const handleSubtopicSelect = (subtopicName) => {
    setSelectedSubtopic(subtopicName);
    if (subtopics[subtopicName]) {
      setContent(subtopics[subtopic].levels);
    }
  };

  return (
    <div className="two-column-page">
      {/* Left Column: Topics and Subtopics */}
      <div className="left-column">
        <h3>Topics</h3>
        <ul>
          {topics.map((topic) => (
            <li key={topic.topicId}>
              <button
                onClick={() => handleTopicSelect(topic.topicId)}
                className={topic.topicId === selectedTopicId ? 'active' : ''}
              >
                {topic.topic}
              </button>
              {selectedTopicId === topic.topicId && (
                <ul className="subtopics-list">
                  {Object.keys(subtopics).map((subtopicName) => (
                    <li key={subtopicName}>
                      <button
                        onClick={() => handleSubtopicSelect(subtopicName)}
                        className={selectedSubtopic === subtopicName ? 'active' : ''}
                      >
                        {subtopicName}
                      </button>
                    </li>
                  ))}
                </ul>
              )}
            </li>
          ))}
        </ul>
      </div>

      {/* Right Column: Content */}
      <div className="right-column">
        {selectedSubtopic ? (
          <div>
            <h2>{selectedSubtopic} - Content</h2>
            {['basic', 'medium', 'advanced'].map((level) => (
              <div key={level}>
                <h3>{level.charAt(0).toUpperCase() + level.slice(1)} Level</h3>
                <ul>
                  {content[level].map((item, index) => (
                    <li key={index}>
                      <h4>{item.title}</h4>
                      <p>{item.description}</p>
                      <a href={item.link} target="_blank" rel="noopener noreferrer">
                        {item.link}
                      </a>
                      <p>Rating: {item.rating}/5</p>
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        ) : selectedTopicId ? (
          <p>Select a subtopic to view its content.</p>
        ) : (
          <p>Select a topic to view subtopics.</p>
        )}
      </div>
    </div>
  );
};

export default TwoColumnPage;
