import React, { useState } from 'react';

const NptelContent = () => {
    const [videoForms, setVideoForms] = useState([]);
    const [videoTitle, setVideoTitle] = useState('');
    const [videoDescription, setVideoDescription] = useState('');
  
    const handleAddVideoForm = () => {
      setVideoForms([...videoForms, { id: videoForms.length + 1, title: '', description: '' }]);
    };
  
    const handleRemoveVideoForm = (id) => {
      setVideoForms(videoForms.filter((form) => form.id !== id));
    };
  
    const handleVideoTitleChange = (id, event) => {
      const updatedForms = videoForms.map((form) => {
        if (form.id === id) {
          return { ...form, title: event.target.value };
        }
        return form;
      });
      setVideoForms(updatedForms);
    };
  
    const handleVideoDescriptionChange = (id, event) => {
      const updatedForms = videoForms.map((form) => {
        if (form.id === id) {
          return { ...form, description: event.target.value };
        }
        return form;
      });
      setVideoForms(updatedForms);
    };
  
    return (
      <div className="container mt-4">
        <h2 className="mb-4">Video Input Form</h2>
        <button type="button" className="btn btn-primary" onClick={handleAddVideoForm}>
          Add Video Form
        </button>
        {videoForms.map((form) => (
          <div key={form.id} className="mb-4">
            <h4>Video {form.id}</h4>
            <form>
              <div className="mb-3">
                <label htmlFor="videoTitle" className="form-label">
                  Video Title
                </label>
                <input
                  type="text"
                  id="videoTitle"
                  className="form-control"
                  value={form.title}
                  onChange={(event) => handleVideoTitleChange(form.id, event)}
                  placeholder="Enter video title"
                />
              </div>
              <div className="mb-3">
                <label htmlFor="videoDescription" className="form-label">
                  Video Description
                </label>
                <textarea
                  id="videoDescription"
                  className="form-control"
                  value={form.description}
                  onChange={(event) => handleVideoDescriptionChange(form.id, event)}
                  placeholder="Enter video description"
                />
              </div>
              <button
                type="button"
                className="btn btn-danger"
                onClick={() => handleRemoveVideoForm(form.id)}
              >
                Remove
              </button>
            </form>
          </div>
        ))}
      </div>
    );
  };
  
export default NptelContent;
