import React, { useState } from "react";
import axios from "axios";
import '../../../Css/NptelContent.css'; // Optional CSS for styling

const NptelContent = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  return (
    <div className="nptel-container">
      <img 
        src="/image/image.png" 
        alt="" 
        width={90} 
        onClick={toggleModal} 
        style={{ cursor: "pointer" }} 
      />

      {isModalOpen && (
        <div className="nptel-modal-overlay">
          <div className="nptel-modal-content">
            <h2>Modal Title</h2>
            <p>This is the content of the modal.</p>
            <button classonClick={toggleModal}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default NptelContent;
