import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../../Css/Home.css'; // Import CSS for styling

const Home = () => {
    const [users, setUsers] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Fetch user details on component mount
    useEffect(() => {
        const fetchUserDetails = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/user');
                setUsers(response.data); // Set user details in state
            } catch (err) {
                setError('Error fetching user details'); // Handle error
            } finally {
                setLoading(false); // Set loading to false
            }
        };

        fetchUserDetails();
    }, []);
console.log(users.results);
    // Render loading, error, or user details
    return (
        <div className="home-container">
            <header className="header">
                <h1>Welcome to the Student Information Portal</h1>
                <p>Empowering Students for Success</p>
            </header>
            <main className="student-profiles">
                {loading ? (
                    <p>Loading user details...</p>
                ) : error ? (
                    <p>{error}</p>
                ) : users.length > 0 ? ( // Check if users array is not empty
                    users.map((user) => ( // Use map to display user details
                        <div className="student-card" key={user.id}> {/* Assuming each user has a unique id */}
                            <h2>{user.name}</h2>
                            <p>Age: {user.name}</p> {/* Assuming user object has an age property */}
                            <p>Major: {user.email}</p> {/* Assuming user object has a major property */}
                            <h3>Achievements:</h3>
                            {/* Add achievements rendering logic here if available */}
                        </div>
                    ))
                ) : (
                    <p>No user details available</p>
                )}
            </main>
            <footer className="footer">
                <p>Contact us: studentinfo@example.com | Phone: (123) 456-7890</p>
            </footer>
        </div>
    );
};

export default Home;
