import React from "react";

const NptelContent = ({ data, onChange, onRemove }) => {
  const handleInputChange = (field, value) => {
    onChange({ ...data, [field]: value });
  };

  return (
    <div className="border p-3 mb-3">
      <h5>NPTEL Content</h5>
      <div className="mb-3">
        <label htmlFor="videoTitle" className="form-label">
          Video Title
        </label>
        <input
          type="text"
          id="videoTitle"
          className="form-control"
          value={data.video_title || ""}
          onChange={(e) => handleInputChange("video_title", e.target.value)}
        />
      </div>
      <div className="mb-3">
        <label htmlFor="videoDescription" className="form-label">
          Video Description
        </label>
        <textarea
          id="videoDescription"
          className="form-control"
          rows="3"
          value={data.video_description || ""}
          onChange={(e) => handleInputChange("video_description", e.target.value)}
        ></textarea>
      </div>
      <div className="mb-3">
        <label htmlFor="videoName" className="form-label">
          Video Name
        </label>
        <input
          type="text"
          id="videoName"
          className="form-control"
          value={data.video_name || ""}
          onChange={(e) => handleInputChange("video_name", e.target.value)}
        />
      </div>
      {onRemove && (
        <button
          type="button"
          className="btn btn-danger"
          onClick={onRemove}
        >
          Remove NPTEL Content
        </button>
      )}
    </div>
  );
};

export default NptelContent;
