import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../../../Css/TwoColumnPage.css';

const TwoColumnPage = ({ selectedSubject }) => {
  const [topicsData, setTopicsData] = useState({});
  const [selectedTopic, setSelectedTopic] = useState('');
  const [selectedSubtopic, setSelectedSubtopic] = useState('');
  const [content, setContent] = useState('');

  // Fetch topics data from the backend
  useEffect(() => {
    const fetchTopicsData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/topics/${selectedSubject}`);
        setTopicsData(response.data);
      } catch (error) {
        console.error('Error fetching topics data:', error);
      }
    };

    if (selectedSubject) {
      fetchTopicsData(); // Fetch data whenever selectedSubject changes
    }
  }, [selectedSubject]);

  // Handle topic selection
  const handleTopicSelect = (topicId) => {
    setSelectedTopic(topicId);
    setSelectedSubtopic('');
    setContent('');
  };

  // Handle subtopic selection
  const handleSubtopicSelect = (subtopic) => {
    setSelectedSubtopic(subtopic);
    setContent(topicsData[selectedTopic].subtopics[subtopic]);
  };

  return (
    <div className="two-column-page">
      {/* Left column: Topics and Subtopics */}
      <div className="left-column">
        <h3>Chapter</h3>
        {Object.keys(topicsData).length > 0 ? (
          <ul>
            {Object.keys(topicsData).map((topicId) => (
              <li key={topicId}>
                <button
                  onClick={() => handleTopicSelect(topicId)}
                  className={topicId === selectedTopic ? 'active' : ''}
                >
                  Topic {topicsData[topicId].topic}
                </button>
                {topicId === selectedTopic && topicsData[topicId].subtopics && (
                  <ul className="subtopics-list">
                    {Object.keys(topicsData[topicId].subtopics).map((subtopic) => (
                      <li key={subtopic}>
                        <button
                          onClick={() => handleSubtopicSelect(subtopic)}
                          className={subtopic === selectedSubtopic ? 'active' : ''}
                        >
                          {subtopic}
                        </button>
                      </li>
                    ))}
                  </ul>
                )}
              </li>
            ))}
          </ul>
        ) : (
          <p>No topics available for the selected subject.</p>
        )}
      </div>

      {/* Right column: Content */}
      <div className="right-column">
        {selectedSubtopic ? (
          <>
            <h2>{selectedSubtopic}</h2>
            {content && (
              <ul>
                {content.map((item, index) => (
                  <li key={index}>
                    <h3>{item.title}</h3>
                    <p>{item.description}</p>
                    <a href={item.url} target="_blank" rel="noopener noreferrer">
                      {item.url}
                    </a>
                    <p>Rating: {item.rating}/5</p>
                  </li>
                ))}
              </ul>
            )}
          </>
        ) : selectedTopic ? (
          <p>Select a subtopic to view its content.</p>
        ) : (
          <p>Select a topic to view its subtopics.</p>
        )}
      </div>
    </div>
  );
};

export default TwoColumnPage;
