import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../../../Css/TwoColumnPage.css';

const TwoColumnPage = ({ selectedSubject }) => {
  const [topics, setTopics] = useState([]);
  const [subtopics, setSubtopics] = useState([]);
  const [selectedTopicId, setSelectedTopicId] = useState(null);
  const [selectedSubtopic, setSelectedSubtopic] = useState(null);
  const [groupedContent, setGroupedContent] = useState({});

  // Fetch topics when subject changes
  useEffect(() => {
    const fetchTopics = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/topics/${selectedSubject}`);
        setTopics(response.data.topics);
        setSubtopics([]);
        setGroupedContent({});
        setSelectedTopicId(null);
        setSelectedSubtopic(null);
      } catch (error) {
        console.error('Error fetching topics:', error);
      }
    };
    if (selectedSubject) fetchTopics();
  }, [selectedSubject]);

  // Fetch subtopics and content when a topic is selected
  const handleTopicSelect = async (topicId) => {
    setSelectedTopicId(topicId);
    setSelectedSubtopic(null);
    setGroupedContent({});
    setSubtopics([]);

    try {
      const subtopicsResponse = await axios.get(`http://localhost:5000/api/subtopics/${topicId}`);
      const fetchedSubtopics = subtopicsResponse.data;

      setSubtopics(fetchedSubtopics);

      // Fetch content for each subtopic
      const contentPromises = fetchedSubtopics.map(async (subtopic) => {
        const contentResponse = await axios.get(`http://localhost:5000/api/content/${subtopic.id}`);
        return { subtopicName: subtopic.subTopic, levels: contentResponse.data };
      });

      const contentArray = await Promise.all(contentPromises);
      const grouped = contentArray.reduce((acc, { subtopicName, levels }) => {
        acc[subtopicName] = levels;
        return acc;
      }, {});

      setGroupedContent(grouped);
    } catch (error) {
      console.error('Error fetching subtopics or content:', error);
    }
  };

  // Handle subtopic selection
  const handleSubtopicSelect = (subtopicName) => {
    setSelectedSubtopic(subtopicName);
  };

  return (
    <div className="two-column-page">
      {/* Left Column: Topics and Subtopics */}
      <div className="left-column">
        <h3>Topics</h3>
        <ul>
          {topics.map((topic) => (
            <li key={topic.id}>
              <button
                onClick={() => handleTopicSelect(topic.id)}
                className={topic.id === selectedTopicId ? 'active' : ''}
              >
                {topic.topic}
              </button>
              {selectedTopicId === topic.id && (
                <ul className="subtopics-list">
                  {subtopics.map((subtopic) => (
                    <li key={subtopic.id}>
                      <button
                        onClick={() => handleSubtopicSelect(subtopic.subTopic)}
                        className={selectedSubtopic === subtopic.subTopic ? 'active' : ''}
                      >
                        {subtopic.subTopic}
                      </button>
                    </li>
                  ))}
                </ul>
              )}
            </li>
          ))}
        </ul>
      </div>

      {/* Right Column: Grouped Content */}
      <div className="right-column">
        {selectedTopicId && (
          <div>
            <h2>Content for Topic ID: {selectedTopicId}</h2>
            {Object.keys(groupedContent).length > 0 ? (
              Object.keys(groupedContent).map((subtopicName) => (
                <div key={subtopicName}>
                  <h3>{subtopicName}</h3>
                  {['basic', 'medium', 'advanced'].map((level) => (
                    <div key={level} className="content-level">
                      <h4>{level.charAt(0).toUpperCase() + level.slice(1)} Level</h4>
                      <ul>
                        {groupedContent[subtopicName][level]?.map((item, index) => (
                          <li key={index} className="content-item">
                            <h5>{item.title}</h5>
                            <p>{item.description}</p>
                            <a href={item.link} target="_blank" rel="noopener noreferrer">
                              {item.link}
                            </a>
                            <p>Rating: {item.rating}/5</p>
                          </li>
                        )) || <p>No content available.</p>}
                      </ul>
                    </div>
                  ))}
                </div>
              ))
            ) : (
              <p>Loading content or no content available for this topic.</p>
            )}
          </div>
        )}
        {!selectedTopicId && <p>Select a topic to view subtopics and content.</p>}
      </div>
    </div>
  );
};

export default TwoColumnPage;
