import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../../Css/Home.css'; // Import CSS for styling

const Home = () => {
    const [user, setUser] = useState(null);

    useEffect(() => {
        // Fetch user details from the backend
        const fetchUserDetails = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/user'); // Replace with your backend endpoint
                setUser(response.data);
            } catch (error) {
                console.error('Error fetching user details:', error);
            }
        };

        fetchUserDetails();
    }, []);

    return (
        <div className="home-container">
            <header className="header">
                <h1>Welcome to the Student Information Portal</h1>
                <p>Empowering Students for Success</p>
            </header>
            <main className="student-profiles">
                {user ? (
                    <div className="student-card">
                        <h2>{user.name}</h2>
                        <p>Age: {user.age}</p>
                        <p>Major: {user.major}</p>
                        <h3>Achievements:</h3>
                        <ul>
                            {user.achievements.map((achievement, index) => (
                                <li key={index}>{achievement}</li>
                            ))}
                        </ul>
                    </div>
                ) : (
                    <p>Loading user details...</p>
                )}
            </main>
            <footer className="footer">
                <p>Contact us: studentinfo@example.com | Phone: (123) 456-7890</p>
            </footer>
        </div>
    );
};

export default Home;
