import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFacebook, faTwitter, faInstagram, faLinkedin } from '@fortawesome/free-brands-svg-icons';

const Home = () => {
  const setLoginType = (type) => {
    document.getElementById('login-type').value = type;
    const studentBtn = document.querySelector('.student-login-btn');
    const facultyBtn = document.querySelector('.faculty-login-btn');

    if (type === 'faculty') {
      studentBtn.classList.remove('active');
      facultyBtn.classList.add('active');
    } else {
      facultyBtn.classList.remove('active');
      studentBtn.classList.add('active');
    }
  };

  return (
    <div className="container-fluid">
      {/* Header Section */}
      <header className="d-flex justify-content-between align-items-center py-3">
        <img src="/image/logo.png" alt="Logo" className="img-fluid" style={{ maxHeight: '60px' }} />
        <ul className="nav">
          <li className="nav-item">
            <a className="nav-link" href="#" id="login-btn" data-bs-toggle="modal" data-bs-target="#loginModal">Login</a>
          </li>
        </ul>
      </header>

      {/* Main Content Section */}
      <div className="row align-items-center">
        <div className="col-md-6">
          <div className="text-center text-md-start">
            <h1 className="display-4 mb-4">Digital Education Platform</h1>
            <p className="lead mb-4">Lorem ipsum dolor sit amet, consectetuer adipiscing elit, sed diam nonummy nibh euismod tincidunt</p>
            <a href="#" className="btn btn-primary btn-lg">Go Live</a>
          </div>
        </div>
        <div className="col-md-6">
          <img src="./image/e-learning.png" alt="Digital Education Platform" className="img-fluid" />
        </div>
      </div>

      {/* Footer Section */}
      <footer className="bg-dark text-white py-4 mt-5">
        <div className="container text-center">
          <ul className="list-inline mb-3">
            <li className="list-inline-item"><a href="./indexmenu/about.php" className="text-white">About Us</a></li>
            <li className="list-inline-item"><a href="./indexmenu/contact.php" className="text-white">Contact Us</a></li>
            <li className="list-inline-item"><a href="#" className="text-white">Terms of Service</a></li>
            <li className="list-inline-item"><a href="#" className="text-white">Privacy Policy</a></li>
          </ul>
          <p>&copy; 2024 Vignan University. All rights reserved.</p>
          <div>
            <a href="https://www.facebook.com/vignanuniversity" target="_blank" className="text-white mx-2"><FontAwesomeIcon icon={faFacebook} /></a>
            <a href="https://twitter.com/Vignanuniv" target="_blank" className="text-white mx-2"><FontAwesomeIcon icon={faTwitter} /></a>
            <a href="https://www.instagram.com/vignanuniversity/" target="_blank" className="text-white mx-2"><FontAwesomeIcon icon={faInstagram} /></a>
            <a href="https://www.linkedin.com/school/vignan-university/" target="_blank" className="text-white mx-2"><FontAwesomeIcon icon={faLinkedin} /></a>
          </div>
        </div>
      </footer>

      {/* Bootstrap Modal for Login */}
      <div className="modal fade" id="loginModal" tabIndex="-1" aria-labelledby="loginModalLabel" aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="loginModalLabel">Login</h5>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
              <div className="btn-group w-100 mb-3" role="group" aria-label="Login Type">
                <button type="button" className="btn btn -outline-primary student-login-btn active" onClick={() => setLoginType('student')}>Student</button>
                <button type="button" className="btn btn-outline-primary faculty-login-btn" onClick={() => setLoginType('faculty')}>Faculty</button>
              </div>
              <form id="login-form" method="post" action="./registration/login.php">
                <input type="hidden" id="login-type" name="login-type" value="student" />
                <div className="mb-3">
                  <label htmlFor="username" className="form-label">Username</label>
                  <input type="text" id="username" name="username" className="form-control" required />
                </div>
                <div className="mb-3">
                  <label htmlFor="password" className="form-label">Password</label>
                  <input type="password" id="password" name="password" className="form-control" required />
                </div>
                <button type="submit" className="btn btn-primary w-100">Login</button>
                <a href="./registration/registration.php" id="register-link" className="d-block text-center mt-3">Don't have an account? Register here!</a>
              </form>
            </div>
          </div>
        </div>
      </div>

      {/* Bootstrap JS and Popper.js */}
      <script src="https://cdn.jsdelivr.net/npm/@popperjs/core@2.11.6/dist/umd/popper.min.js"></script>
      <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0-alpha1/dist/js/bootstrap.min.js"></script>
    </div>
  );
};

export default Home;
