import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../../../Css/TwoColumnPage.css';

const TwoColumnPage = ({ selectedSubject }) => {
  const [topics, setTopics] = useState([]);
  const [selectedTopicId, setSelectedTopicId] = useState(null);
  const [selectedSubtopic, setSelectedSubtopic] = useState(null);

  // Fetch topics and subtopics when the subject changes
  useEffect(() => {
    const fetchTopics = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/topics/${selectedSubject}`);
        setTopics(response.data.topics);
      } catch (error) {
        console.error('Error fetching topics:', error);
      }
    };

    if (selectedSubject) fetchTopics();
  }, [selectedSubject]);

  return (
    <div className="two-column-page">
      {/* Left Column: Topics and Subtopics */}
      <div className="left-column">
        <h3>Topics</h3>
        <ul>
          {topics.map((topic) => (
            <li key={topic.topicId}>
              <button
                onClick={() => setSelectedTopicId(topic.topicId)}
                className={topic.topicId === selectedTopicId ? 'active' : ''}
              >
                {topic.topicName}
              </button>
              {selectedTopicId === topic.topicId && (
                <ul className="subtopics-list">
                  {topic.subTopics.map((subTopicName, index) => (
                    <li key={index}>
                      <button
                        onClick={() => setSelectedSubtopic(subTopicName)}
                        className={selectedSubtopic === subTopicName ? 'active' : ''}
                      >
                        {subTopicName}
                      </button>
                    </li>
                  ))}
                </ul>
              )}
            </li>
          ))}
        </ul>
      </div>

      {/* Right Column: Content */}
      <div className="right-column">
        {selectedSubtopic ? (
          <div>
            <h2>{selectedSubtopic} - Content</h2>
            <p>Content for {selectedSubtopic} will go here.</p>
          </div>
        ) : selectedTopicId ? (
          <p>Select a subtopic to view its content.</p>
        ) : (
          <p>Select a topic to view subtopics.</p>
        )}
      </div>
    </div>
  );
};

export default TwoColumnPage;
