import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../../../../Css/RatingTooltip.css';

const Rating = ({ item }) => {
    const [showTooltip, setShowTooltip] = useState(false);
    const [feedbackMessage, setFeedbackMessage] = useState("");
    const [ratingDistribution, setRatingDistribution] = useState([]);
    const [username, setUsername] = useState("");

    // Retrieve username from Local Storage
    useEffect(() => {
        const storedUsername = localStorage.getItem('username');
        if (storedUsername) {
            setUsername(storedUsername);
            console.log(username);
        }
    }, []);
    // Fetch rating distribution for the item
    useEffect(() => {
        const fetchRatingDistribution = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/rating/${item}`);
                setRatingDistribution(response.data); // Assume the API returns an array of counts for ratings [1, 2, 3, 4, 5]
            } catch (error) {
                console.error("Error fetching rating distribution:", error);
            }
        };

        fetchRatingDistribution();
    }, [item]);

    const handleRatingSubmit = async (e, itemId) => {
        e.preventDefault();
        const ratingValue = e.target.ratingValue.value;

        try {
            const response = await axios.post('http://localhost:5000/api/rating', {
                itemId,
                rating: ratingValue,
            });

            if (response.status === 200) {
                setFeedbackMessage(`Thank you for rating item ${itemId} as ${ratingValue} stars!`);
            } else {
                setFeedbackMessage("Failed to submit rating. Please try again.");
            }
        } catch (error) {
            console.error("Error submitting rating:", error);
            if (error.response && error.response.status === 400) {
                setFeedbackMessage('You have already rated this item');
            } else {
                setFeedbackMessage('An error occurred. Please try again.');
            }
        }

        e.target.reset(); // Reset the form after submission
    };

    const renderBarGraph = () => {
        const totalRatings = ratingDistribution.reduce((acc, count) => acc + count, 0);
        return (
            <div className="bar-graph">
                {ratingDistribution.map((count, index) => (
                    <div key={index} className="bar-graph-item">
                        <div
                            className="bar"
                            style={{
                                height: `${(count / (totalRatings || 1)) * 100}%`,
                                backgroundColor: "#007bff",
                            }}
                        ></div>
                        <span className="bar-label">{index + 1}‚≠ê</span>
                        <span className="bar-count">{count}</span>
                    </div>
                ))}
            </div>
        );
    };

    return (
        <div>
            <p className="card-text">ID: {item}</p>

            <div className="ratingtooltip">
                <form
                    onSubmit={(e) => handleRatingSubmit(e, item)}
                    className="d-flex align-items-center"
                    style={{ gap: "0.5em" }}
                >
                    <label htmlFor={`ratingValue-${item}`} className="mb-0">
                        Rate:
                    </label>
                    <input
                        type="number"
                        id={`ratingValue-${item}`}
                        name="ratingValue"
                        min="1"
                        max="5"
                        required
                        className="form-control"
                        style={{ width: "50px" }}
                        onMouseOver={() => setShowTooltip(true)} // Show tooltip on hover
                        onMouseOut={() => setShowTooltip(false)} // Hide tooltip on mouse out
                    />
                    <button type="submit" className="btn btn-primary btn-sm">
                        Submit
                    </button>

                    {showTooltip && (
                        <div className="tooltip-bar-graph">
                            <p>Rating Distribution:</p>
                            {renderBarGraph()}
                        </div>
                    )}
                </form>
            </div>

            {feedbackMessage && <p>{feedbackMessage}</p>}
        </div>
    );
};

export default Rating;
