import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Make sure to import axios

const Rating = ({ item }) => {
    const [feedbackMessage, setFeedbackMessage] = useState("");
    const [isFormVisible, setIsFormVisible] = useState(true); // Track visibility of the form

    // Check if rating already exists for the item on page load
    useEffect(() => {
        const checkIfRated = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/rating/${item}`);
                if (response.data && response.data.rating) {
                    setIsFormVisible(false); // Hide the form if a rating exists
                }
            } catch (error) {
                console.error("Error checking rating:", error);
            }
        };

        checkIfRated();
    }, [item]); // Trigger check when component mounts

    const handleRatingSubmit = async (e, itemId) => {
        e.preventDefault();
        const ratingValue = e.target.ratingValue.value;

        // API call to submit rating using Axios
        try {
            const response = await axios.post('http://localhost:5000/api/rating', {
                itemId,
                rating: ratingValue,
            });

            if (response.status === 200) {
                setFeedbackMessage(`Thank you for rating item ${itemId} as ${ratingValue} stars!`);
                setIsFormVisible(false); // Hide the form after successful submission
            } else {
                setFeedbackMessage("Failed to submit rating. Please try again.");
            }
        } catch (error) {
            console.error("Error submitting rating:", error);
            setFeedbackMessage("An error occurred. Please try again later.");
        }

        e.target.reset(); // Reset the form after submission
    };

    return (
        <div>
            <p className="card-text">ID: {item}</p>

            {/* Show form only if it's visible */}
            {isFormVisible && (
                <form
                    onSubmit={(e) => handleRatingSubmit(e, item)}
                    className="d-flex align-items-center"
                    style={{ gap: "0.5em" }}
                >
                    <label htmlFor={`ratingValue-${item}`} className="mb-0">
                        Rate:
                    </label>
                    <input
                        type="number"
                        id={`ratingValue-${item}`}
                        name="ratingValue"
                        min="1"
                        max="5"
                        required
                        className="form-control"
                        style={{ width: "50px" }}
                    />
                    <button type="submit" className="btn btn-primary btn-sm">
                        Submit
                    </button>
                </form>
            )}

            {/* Feedback Message */}
            {feedbackMessage && <p>{feedbackMessage}</p>}
        </div>
    );
};

export default Rating;
