import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { useLocation } from "react-router-dom";
import NptelContent from "./NptelContent";
import Link from "./Link";

const AddTopic = () => {
    const location = useLocation();
    const { course, branch, semester, subject } = location.state || {};
    const [subTopic, setSubTopic] = useState("");
    const [levels, setLevels] = useState({
        basic: [],
        medium: [],
        advanced: [],
    });
    const [topics, setTopics] = useState([]);
    const [selectedTopic, setSelectedTopic] = useState("");
    const [newTopic, setNewTopic] = useState("");
    const [isAddingNewTopic, setIsAddingNewTopic] = useState(false);
    const [loading, setLoading] = useState(false);
    const [nptelData, setNptelData] = useState([]); // Store NPTEL video data

    const navigate = useNavigate();

    useEffect(() => {
        const fetchTopics = async () => {
            setLoading(true);
            try {
                if (subject) {
                    const response = await axios.get(
                        `http://localhost:5000/api/topics?subjectId=${subject}`
                    );
                    setTopics(response.data);
                }
            } catch (error) {
                console.error("Error fetching topics:", error);
            } finally {
                setLoading(false);
            }
        };
        fetchTopics();
    }, [subject]);

    const handleVideoDataChange = (updatedData) => {
        setNptelData(updatedData);
        
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!selectedTopic && !newTopic) {
            alert("Please select a topic or enter a new topic.");
            return;
        }
        if (!subTopic) {
            alert("Please enter a subtopic.");
            return;
        }

        const requestData = {
            subTopic,
            selectedTopic: isAddingNewTopic ? newTopic : selectedTopic,
            isAddingNewTopic,
            courseId: course,
            branchId: branch,
            semesterId: semester,
            subjectId: subject,
            levels,
            nptelVideos: nptelData,
            
        };
        try {
            const response = await axios.post(
                "http://localhost:5000/api/topics/add-topic",
                requestData
            );
            console.log("Topic and links added successfully:", response.data);
            navigate(-1);
        } catch (error) {
            console.error("Error adding topic:", error);
            alert("An error occurred while adding the topic. Please try again.");
        }
    };
    const handleInputChange = (level, index, field, value) => {
      setLevels((prev) => ({
        ...prev,
        [level]: prev[level].map((item, i) =>
          i === index ? { ...item, [field]: value } : item
        ),
      }));
    };
    return (
        <div className="container mt-4">
            <h2 className="mb-4">Add Topic</h2>
            {loading ? (
                <p>Loading topics...</p>
            ) : (
                <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="topic" className="form-label">
                            Select Topic
                        </label>
                        <select
                            id="topic"
                            className="form-select"
                            value={selectedTopic}
                            onChange={(e) => {
                                const value = e.target.value;
                                setSelectedTopic(value);
                                setIsAddingNewTopic(value === "add_new_topic");
                            }}
                        >
                            <option value="">-- Select a Topic --</option>
                            {topics.map((topic) => (
                                <option key={topic.id} value={topic.topic}>
                                    {topic.topic}
                                </option>
                            ))}
                            <option value="add_new_topic">Add New Topic</option>
                        </select>
                    </div>

                    {isAddingNewTopic && (
                        <div className="mb-3">
                            <label htmlFor="newTopic" className="form-label">
                                New Topic Name
                            </label>
                            <input
                                type="text"
                                id="newTopic"
                                className="form-control"
                                value={newTopic}
                                onChange={(e) => setNewTopic(e.target.value)}
                            />
                        </div>
                    )}

                    <div className="mb-3">
                        <label htmlFor="subTopic" className="form-label">
                            Subtopic Name
                        </label>
                        <input
                            type="text"
                            id="subTopic"
                            className="form-control"
                            value={subTopic}
                            onChange={(e) => setSubTopic(e.target.value)}
                        />
                    </div>

                    {["basic", "medium", "advanced"].map((level) => (
                        <div key={level} className="mb-4">
                            <h4 className="text-capitalize">{level} Level</h4>
                            {levels[level].map((item, index) => (
                                <Link
                                    key={`${level}-${index}`}
                                    level={level}
                                    index={index}
                                    data={item}
                                    handleInputChange={(field, value) =>
                                        setLevels((prev) => ({
                                            ...prev,
                                            [level]: prev[level].map((link, i) =>
                                                i === index ? { ...link, [field]: value } : link
                                            ),
                                        }))
                                    }
                                    removeLink={() =>
                                        setLevels((prev) => ({
                                            ...prev,
                                            [level]: prev[level].filter((_, i) => i !== index),
                                        }))
                                    }
                                />
                            ))}
                            <button
                                type="button"
                                className="btn btn-primary"
                                onClick={() =>
                                    setLevels((prev) => ({
                                        ...prev,
                                        [level]: [
                                            ...prev[level],
                                            { title: "", link: "", description: "", rating: 0 },
                                        ],
                                    }))
                                }
                            >
                                Add {level.charAt(0).toUpperCase() + level.slice(1)} Link
                            </button>
                        </div>
                    ))}

                    <NptelContent
                        subject={subject}
                        onVideoDataChange={handleVideoDataChange}
                    />

                    <button type="submit" className="btn btn-success">
                        Save Topic
                    </button>
                </form>
            )}
        </div>
    );
};

export default AddTopic;
