import React, { useState, useEffect } from "react";
import axios from "axios";

const Rating = ({ item }) => {
  const [rating, setRating] = useState(null);  // To store the rating value from the backend
  const [isRated, setIsRated] = useState(false);  // To track if the user has already rated
  const [feedbackMessage, setFeedbackMessage] = useState("");

  // Fetch the existing rating when the component is mounted
  useEffect(() => {
    const fetchRating = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/rating/${item}`);
        if (response.data.rating !== null) {
          setRating(response.data.rating); // Store the existing rating
          setIsRated(true); // Set the form as disabled
        }
      } catch (error) {
        console.error("Error fetching rating:", error);
      }
    };

    fetchRating();
  }, [item]);

  // Handle rating form submission
  const handleRatingSubmit = async (e) => {
    e.preventDefault();
    const ratingValue = e.target.ratingValue.value;

    // API call to submit rating
    try {
      const response = await axios.post("http://localhost:5000/api/rating", {
        itemId: item,
        rating: ratingValue,
      });

      if (response.status === 200) {
        setFeedbackMessage(`Thank you for rating ${item} as ${ratingValue} stars!`);
        setIsRated(true);  // Disable the form after successful submission
        setRating(ratingValue); // Update the rating state
      } else {
        setFeedbackMessage("Failed to submit rating. Please try again.");
      }
    } catch (error) {
      console.error("Error submitting rating:", error);
      setFeedbackMessage("An error occurred. Please try again later.");
    }

    e.target.reset(); // Reset the form after submission
  };

  return (
    <div>
      <p className="card-text">ID: {item}</p>

      {/* Display the rating if already rated */}
      {isRated ? (
        <p>You've rated this item: {rating} stars</p>
      ) : (
        <form
          onSubmit={handleRatingSubmit}
          className="d-flex align-items-center"
          style={{ gap: "0.5em" }}
        >
          <label htmlFor={`ratingValue-${item}`} className="mb-0">
            Rate:
          </label>
          <input
            type="number"
            id={`ratingValue-${item}`}
            name="ratingValue"
            min="1"
            max="5"
            required
            className="form-control"
            style={{ width: "50px" }}
            disabled={isRated}  // Disable input if the user has already rated
          />
          <button type="submit" className="btn btn-primary btn-sm" disabled={isRated}> {/* Disable submit button if already rated */}
            Submit
          </button>
        </form>
      )}

      {feedbackMessage && <p>{feedbackMessage}</p>}
    </div>
  );
};

export default Rating;
