import React, { useState } from "react";
import axios from "axios";
import '../../../Css/NptelContent.css'; // Optional CSS for styling

const NptelContent = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  const [folderPath, setFolderPath] = useState("");
  const [videos, setVideos] = useState([]);
  const [error, setError] = useState("");

  const fetchVideos = async () => {
      try {
          const response = await axios.get("http://localhost:5000/api/videos", {
              params: { folderPath },
          });
          setVideos(response.data);
          setError(""); // Clear any previous errors
      } catch (err) {
          setVideos([]);
          setError(err.response?.data?.error || "Failed to fetch videos");
      }
  };



  return (
    <div className="nptel-container">
      <img
        src="/image/image.png"
        alt=""
        width={90}
        onClick={toggleModal}
        style={{ cursor: "pointer" }}
      />

      {isModalOpen && (
        <div className="nptel-modal-overlay">
          <div className="nptel-modal-content">
            <h2>Modal Title</h2>
            <p>This is the content of the modal.</p>
            


            
            <button className="nptel-model-close" onClick={toggleModal}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default NptelContent;
