import React from 'react';
import './ToolTip.css';
import ModelVideo from './ModelVideo';
const ToolTip = ({ item }) => {
  const displayTitle = item.title.length > 15 ? item.title.slice(0, 12) + '...' : item.title;
    return (
        <div className="tooltip">
            <span className="tooltip-title" style={{ display: 'inline-block', marginRight: '10px' }}>
                        {/* {item.type === "video" ? "üé•" : "üìÑ"}  */}
                        <ModelVideo item={item} />
                        {/* {displayTitle} */}
                        <div className="custom-tooltip">
                          {item.description || "No additional details available."}
                        </div>
                        
                      </span>&nbsp;&nbsp;&nbsp;&nbsp;
                      <span className="tooltip-title" >
                      {"‚≠ê".repeat(item.rating)}
                        <div className="custom-tooltip">
                          {item.rating || "No additional details available."}/5
                        </div>
                        
                      </span>
        </div>
    );
}
export default ToolTip;
