import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Table.css";
import "../../../Css/VideoModal.css";
import ToolTip from "./Function/ToolTip";
import Rating from "./Function/Rating";


const NptelContent = () => {
  const [videoUrl, setVideoUrl] = useState(null);
  const [feedbackMessage, setFeedbackMessage] = useState("");
  const [nptelContent, setNptelContent] = useState([]);
  
  useEffect(() => {
    // Fetch NPTEL content on component mount
    axios
      .get("http://192.168.68.12:9080/Tv")
      .then((response) => {
        setNptelContent(response.data);
      })
      .catch((error) => {
        console.error("Error fetching NPTEL content:", error);
        setFeedbackMessage("Failed to fetch NPTEL content.");
      });
  }, []);

  const isYouTubeLink = (link) => {
    return link && (link.includes("youtube.com") || link.includes("youtu.be"));
  };

  const handleYouTubeClick = (link) => {
    const embedUrl = link.includes("youtube.com")
      ? link.replace("watch?v=", "embed/")
      : link.replace("youtu.be/", "www.youtube.com/embed/");
    setVideoUrl(embedUrl);
  };

  const closeModal = () => {
    setVideoUrl(null);
  };

  const truncateText = (text, maxLength) => {
    if (!text || text.length <= maxLength) return text;
    return text.substring(0, maxLength) + "...";
  };

  const renderCardContent = (item) => (
    <div key={item.id} className="card mb-3">
      <div className="card-body">
        <ToolTip text={item.title || "No Title Available"}>
          <h3
            className="card-title"
            style={{
              whiteSpace: "nowrap",
              overflow: "hidden",
              textOverflow: "ellipsis",
            }}
          >
            {truncateText(item.title || "No Title Available", 20)}
          </h3>
        </ToolTip>
        <ToolTip text={item.description || "No Description Available"}>
          <p
            className="card-text"
            style={{
              whiteSpace: "nowrap",
              overflow: "hidden",
              textOverflow: "ellipsis",
            }}
          >
            {truncateText(item.description || "No Description Available", 30)}
          </p>
        </ToolTip>
        <ToolTip text={`Rating: ${item.rating} out of 5`}>
          <p>Rating: {"⭐".repeat(item.rating) || "N/A"}</p>
        </ToolTip>
        {isYouTubeLink(item.link) ? (
          <img
            src="/image/youtube_logo.png"
            alt="YouTube"
            className="youtube-logo"
            onClick={() => handleYouTubeClick(item.link)}
          />
        ) : (
          item.link && (
            <div className="d-flex align-items-center">
              <img
                src="/image/link_logo.png"
                alt="Link"
                className="me-2"
                style={{ width: "24px", cursor: "pointer" }}
              />
              <a
                href={item.link}
                target="_blank"
                rel="noopener noreferrer"
                className="btn btn-primary btn-sm"
              >
                Visit Link
              </a>
            </div>
          )
        )}
        <Rating item={item.id} />
      </div>
    </div>
  );

  return (
    <div className="containers">
      <div className="chapter">
        <div className="topic">
          <table className="table table-bordered">
            <thead className="table-dark">
              <tr>
                <th>Basic Level</th>
                <th>Intermediate Level</th>
                <th>Advanced Level</th>
                <th>Local NPTEL</th>
                <th>Live Video</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>
                  {levels.basic && levels.basic.length > 0 ? (
                    levels.basic.map(renderCardContent)
                  ) : (
                    <p>No Basic Level Content Available</p>
                  )}
                </td>
                <td>
                  {levels.medium && levels.medium.length > 0 ? (
                    levels.medium.map(renderCardContent)
                  ) : (
                    <p>No Medium Level Content Available</p>
                  )}
                </td>
                <td>
                  {levels.advanced && levels.advanced.length > 0 ? (
                    levels.advanced.map(renderCardContent)
                  ) : (
                    <p>No Advanced Level Content Available</p>
                  )}
                </td>
                <td>
                  {nptelContent.length > 0 ? (
                    nptelContent.map((channel, index) => (
                      <div key={index} className="nptel-channel">
                        <p>{channel.title}</p>
                        <button
                          className="btn btn-primary btn-sm"
                          onClick={() => setVideoUrl(channel.link)}
                        >
                          Watch Live
                        </button>
                      </div>
                    ))
                  ) : (
                    <p>Loading NPTEL Content...</p>
                  )}
                </td>
                <td></td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
      {videoUrl && <VideoModal videoUrl={videoUrl} onClose={closeModal} />}
      {feedbackMessage && <p className="feedback-message">{feedbackMessage}</p>}
    </div>
  );
};

export default NptelContent;
