import React, { useState, useEffect } from "react";
import Link from "./Link"; // Ensure Link component is correctly implemented
import axios from "axios"; // To make HTTP requests
import { useNavigate } from "react-router-dom"; // Import useNavigate for redirection
import { useLocation } from "react-router-dom";

const AddTopic = () => {
  const location = useLocation();
  const { course, branch, semester, subject } = location.state || {};
  // console.log(course, branch, semester, subject);

  const [topics, setTopics] = useState([]);
  const [selectedTopic, setSelectedTopic] = useState("");
  const [topicName, setTopicName] = useState(""); // For new topic name
  const [subTopic, setSubTopic] = useState("");
  const [levels, setLevels] = useState({
    basic: [],
    medium: [],
    advanced: [],
  });
  const navigate = useNavigate(); // Initialize the useNavigate hook

  useEffect(() => {
    // Fetch existing topics from the backend
    const fetchTopics = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/topics`, {
          params: { course, branch, semester, subject },
        });
        setTopics(response.data);
      } catch (error) {
        console.error("Error fetching topics:", error);
      }
    };
    fetchTopics();
  }, [course, branch, semester, subject]);

  const fetchTopicDetails = async (subject) => {
    try {
      console.log(subject);
      const response = await axios.get(`http://localhost:5000/api/topics/${subject}`);
      const topic = response.data;
      console.log("Topic details:", topic);
      setTopicName(topic.name || ""); // Set existing topic name
      setSubTopic(topic.subTopic || "");
      setLevels(topic.levels || { basic: [], medium: [], advanced: [] });
    } catch (error) {
      console.error("Error fetching topic details:", error);
    }
  };

  const handleTopicChange = (e) => {
    const topicId = e.target.value;
    setSelectedTopic(topicId);

    if (topicId === "new") {
      // Reset fields for adding a new topic
      setTopicName("");
      setSubTopic("");
      setLevels({ basic: [], medium: [], advanced: [] });
    } else if (topicId) {
      // Fetch details of the selected topic
      consol
      fetchTopicDetails(subject);

    }
  };

  const addLink = (level) => {
    setLevels((prev) => ({
      ...prev,
      [level]: [...prev[level], { title: "", link: "", description: "", rating: 0 }],
    }));
  };

  const removeLink = (level, index) => {
    setLevels((prev) => ({
      ...prev,
      [level]: prev[level].filter((_, i) => i !== index),
    }));
  };

  const handleInputChange = (level, index, field, value) => {
    setLevels((prev) => ({
      ...prev,
      [level]: prev[level].map((item, i) =>
        i === index ? { ...item, [field]: value } : item
      ),
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const requestData = {
      topicName: selectedTopic === "new" ? topicName : undefined,
      subTopic,
      courseId: course,
      branchId: branch,
      semesterId: semester,
      subjectId: subject,
      levels,
    };

    try {
      const response = await axios.post('http://localhost:5000/api/topics/add-topic', requestData);
      console.log('Topic and links added successfully:', response.data);

      // Navigate back to the previous page after successful submission
      navigate(-1); // This will take the user back to the previous page
    } catch (error) {
      console.error('Error adding topic:', error);
    }
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">Add or Edit Topic</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="existingTopic" className="form-label">
            Select Topic
          </label>
          <select
            id="existingTopic"
            className="form-select"
            value={selectedTopic}
            onChange={handleTopicChange}
          >
            <option value="">-- Select an existing topic --</option>
            {topics.map((topic) => (
              <option key={topic.id} value={topic.id}>
                {topic.name}
              </option>
            ))}
            <option value="new">Add New Topic</option>
          </select>
        </div>

        {selectedTopic === "new" && (
          <div className="mb-3">
            <label htmlFor="newTopicName" className="form-label">
              Topic Name
            </label>
            <input
              type="text"
              id="newTopicName"
              className="form-control"
              value={topicName}
              onChange={(e) => setTopicName(e.target.value)}
            />
          </div>
        )}

        {(selectedTopic || selectedTopic === "new") && (
          <>
            <div className="mb-3">
              <label htmlFor="subTopic" className="form-label">
                Subtopic Name
              </label>
              <input
                type="text"
                id="subTopic"
                className="form-control"
                value={subTopic}
                onChange={(e) => setSubTopic(e.target.value)}
              />
            </div>

            {["basic", "medium", "advanced"].map((level) => (
              <div key={level} className="mb-4">
                <h4 className="text-capitalize">{level} Level</h4>
                {levels[level].map((item, index) => (
                  <Link
                    key={`${level}-${index}`}
                    level={level}
                    index={index}
                    data={item}
                    handleInputChange={handleInputChange}
                    removeLink={removeLink}
                  />
                ))}
                <button
                  type="button"
                  className="btn btn-primary"
                  onClick={() => addLink(level)}
                >
                  Add {level.charAt(0).toUpperCase() + level.slice(1)} Link
                </button>
              </div>
            ))}
          </>
        )}

        <button type="submit" className="btn btn-success" disabled={!subTopic || (selectedTopic === "new" && !topicName)}>
          Save Topic
        </button>
      </form>
    </div>
  );
};

export default AddTopic;
