import React, { useState, useEffect } from 'react';
import axios from 'axios';

const NptelContent = ({ subject }) => {
    const [videoForms, setVideoForms] = useState([]);
    const [videoNames, setVideoNames] = useState([]);

    const handleAddVideoForm = () => {
        setVideoForms([...videoForms, { id: videoForms.length + 1, title: '', description: '', video: '' }]);
    };

    const handleRemoveVideoForm = (id) => {
        setVideoForms(videoForms.filter((form) => form.id !== id));
    };

    const handleVideoTitleChange = (id, event) => {
        const updatedForms = videoForms.map((form) => {
            if (form.id === id) {
                return { ...form, title: event.target.value };
            }
            return form;
        });
        setVideoForms(updatedForms);
    };

    const handleVideoDescriptionChange = (id, event) => {
        const updatedForms = videoForms.map((form) => {
            if (form.id === id) {
                return { ...form, description: event.target.value };
            }
            return form;
        });
        setVideoForms(updatedForms);
    };

    const handleVideoChange = (id, event) => {
        const updatedForms = videoForms.map((form) => {
            if (form.id === id) {
                return { ...form, video: event.target.value };
            }
            return form;
        });
        setVideoForms(updatedForms);
    };

    // Fetch video names from the backend on component mount
    useEffect(() => {
        const fetchVideoNames = async () => {
            try {
                console.log('Subject:', subject);
                const response = await axios.get(`http://localhost:5000/api/nptelvideos/${subject}`);
                setVideoNames(response.data);
            } catch (error) {
                console.error('Error fetching video names:', error);
            }
        };
        fetchVideoNames();
    }, [subject]);




    return (
        <div className="container mt-4">
            {videoForms.map((form) => (
                <div key={form.id} className="border p-3 mb-3">
                    <div className="row mb-2">
                        <div className="col-md-6">
                            <label htmlFor={`video-title-${form.id}`}>Video Title</label>
                            <input
                                type="text"
                                id={`video-title-${form.id}`}
                                className="form-control"
                                value={form.title}
                                onChange={(event) => handleVideoTitleChange(form.id, event)}
                                placeholder="Enter video title"
                            />
                        </div>
                        <div className="col-md-6">
                            <label htmlFor={`video-description-${form.id}`}>Video Description</label>
                            <textarea
                                id={`video-description-${form.id}`}
                                className="form-control"
                                value={form.description}
                                onChange={(event) => handleVideoDescriptionChange(form.id, event)}
                                placeholder="Enter video description"
                            />
                        </div>
                    </div>
                    <div className="row mb-2">
                        <div className="col-md-6">
                            <label>Video Name:</label>
                            <select
                                className="form-control"
                                value={form.video}
                                onChange={(event) => handleVideoChange(form.id, event)}
                                required
                            >
                                <option value="">--Select a Video--</option>
                                {videoNames && videoNames.length > 0 ? (
                                    videoNames.map((video, index) => (
                                        <option key={index} value={video}>
                                            {video}
                                        </option>
                                    ))
                                ) : (
                                    <option disabled>No videos available</option>
                                )}

                            </select>

                        </div>
                    </div>
                    <button
                        type="button"
                        className="btn btn-danger"
                        onClick={() => handleRemoveVideoForm(form.id)}
                    >
                        Remove
                    </button>
                </div>
            ))}
            <button type="button" className="btn btn-primary mb-3" onClick={handleAddVideoForm}>
                Add Nptel Video
            </button>
        </div>
    );
};

export default NptelContent;
