import React, { useState } from 'react';

const Login = () => {
  const [loginType, setLoginType] = useState('student'); // State to track login type
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLoginType = (type) => {
    setLoginType(type);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log('Login Type:', loginType);
    console.log('Username:', username);
    console.log('Password:', password);

    // Add your login API logic here
   
    fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password, loginType }),
      })
        .then((response) => response.json())
        .then((data) => {
          if (data.message === 'Login successful!') {
            alert('Login successful!');
            // Redirect to the dashboard or other page
          } else {
            alert(data.message);
          }
        })
        .catch((error) => console.error('Error during login:', error));
        
    };

  return (
    <div
      className="modal fade"
      id="loginModal"
      tabIndex="-1"
      aria-labelledby="loginModalLabel"
      aria-hidden="true"
    >
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title" id="loginModalLabel">
              Login
            </h5>
            <button
              type="button"
              className="btn-close"
              data-bs-dismiss="modal"
              aria-label="Close"
            ></button>
          </div>
          <div className="modal-body">
            {/* Login Type Buttons */}
            <div
              className="btn-group w-100 mb-3"
              role="group"
              aria-label="Login Type"
            >
              <button
                type="button"
                className={`btn btn-outline-primary ${
                  loginType === 'student' ? 'active' : ''
                }`}
                onClick={() => handleLoginType('student')}
              >
                Student
              </button>
              <button
                type="button"
                className={`btn btn-outline-primary ${
                  loginType === 'faculty' ? 'active' : ''
                }`}
                onClick={() => handleLoginType('faculty')}
              >
                Faculty
              </button>
            </div>

            {/* Login Form */}
            <form onSubmit={handleSubmit}>
              <input
                type="hidden"
                name="login-type"
                value={loginType}
                readOnly
              />
              <div className="mb-3">
                <label htmlFor="username" className="form-label">
                  Username
                </label>
                <input
                  type="text"
                  id="username"
                  name="username"
                  className="form-control"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>
              <div className="mb-3">
                <label htmlFor="password" className="form-label">
                  Password
                </label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  className="form-control"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              <button type="submit" className="btn btn-primary w-100">
                Login
              </button>
              <a
                href="/registration"
                id="register-link"
                className="d-block text-center mt-3"
              >
                Don't have an account? Register here!
              </a>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
