import React, { useState, useEffect } from 'react';
import axios from 'axios';

const NptelContent = ({ subject, onVideoDataChange }) => {
    const [videoForms, setVideoForms] = useState([]);
    const [videoNames, setVideoNames] = useState([]);

    const handleAddVideoForm = () => {
        const newForm = { id: videoForms.length + 1, title: '', description: '', video: '' };
        const updatedForms = [...videoForms, newForm];
        setVideoForms(updatedForms);
        onVideoDataChange(updatedForms); // Notify parent of change
    };

    const handleRemoveVideoForm = (id) => {
        const updatedForms = videoForms.filter((form) => form.id !== id);
        setVideoForms(updatedForms);
        onVideoDataChange(updatedForms); // Notify parent of change
    };

    const handleVideoChange = (id, field, value) => {
        const updatedForms = videoForms.map((form) =>
            form.id === id ? { ...form, [field]: value } : form
        );
        setVideoForms(updatedForms);
        onVideoDataChange(updatedForms); // Notify parent of change
    };

    // Fetch video names from the backend on component mount
    useEffect(() => {
        const fetchVideoNames = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/nptelvideos/${subject}`);
                setVideoNames(response.data);
            } catch (error) {
                console.error('Error fetching video names:', error);
            }
        };
        fetchVideoNames();
    }, [subject]);

    return (
        <div className="container mt-4">
            {videoForms.map((form) => (
                <div key={form.id} className="border p-3 mb-3">
                    <div className="mb-3">
                        <label htmlFor={`video-title-${form.id}`} className="form-label">
                            Video Title
                        </label>
                        <input
                            type="text"
                            id={`video-title-${form.id}`}
                            className="form-control"
                            value={form.title}
                            onChange={(e) => handleVideoChange(form.id, 'title', e.target.value)}
                            placeholder="Enter video title"
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor={`video-description-${form.id}`} className="form-label">
                            Video Description
                        </label>
                        <textarea
                            id={`video-description-${form.id}`}
                            className="form-control"
                            value={form.description}
                            onChange={(e) => handleVideoChange(form.id, 'description', e.target.value)}
                            placeholder="Enter video description"
                        />
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Select Video</label>
                        <select
                            className="form-control"
                            value={form.video}
                            onChange={(e) => handleVideoChange(form.id, 'video', e.target.value)}
                        >
                            <option value="">--Select a Video--</option>
                            {videoNames.map((video, index) => (
                                <option key={index} value={video}>
                                    {video}
                                </option>
                            ))}
                        </select>
                    </div>
                    <button
                        type="button"
                        className="btn btn-danger"
                        onClick={() => handleRemoveVideoForm(form.id)}
                    >
                        Remove Video
                    </button>
                </div>
            ))}
            <button type="button" className="btn btn-primary" onClick={handleAddVideoForm}>
                Add Video Form
            </button>
        </div>
    );
};

export default NptelContent;
