import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../../../Css/TwoColumnPage.css';

const TwoColumnPage = ({ selectedSubject }) => {
  const [topicsData, setTopicsData] = useState([]);
  const [selectedTopic, setSelectedTopic] = useState(null);
  const [selectedSubtopic, setSelectedSubtopic] = useState(null);
  const [content, setContent] = useState(null);

  // Fetch topics and subtopics based on selected subject
  useEffect(() => {
    const fetchTopics = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/topics/${selectedSubject}`);
        setTopicsData(response.data);
        console.log('Topics data:', Top);
      } catch (error) {
        console.error('Error fetching topics data:', error);
      }
    };

    if (selectedSubject) {
      fetchTopics();
    }
  }, [selectedSubject]);

  // Handle topic selection
  const handleTopicSelect = (topicId) => {
    setSelectedTopic(topicId);
    setSelectedSubtopic(null);  // Reset subtopic when a new topic is selected
    setContent(null); // Reset content
  };

  // Handle subtopic selection
  const handleSubtopicSelect = (subtopic) => {
    setSelectedSubtopic(subtopic);
    const topic = topicsData.find((topic) => topic.id === selectedTopic);
    const subtopicContent = topic.subtopics.find((st) => st.subTopic === subtopic);
    setContent(subtopicContent ? subtopicContent.links : []);
  };

  return (
    <div className="two-column-page">
      {/* Left column: Topics and Subtopics */}
      <div className="left-column">
        <h3>Topics</h3>
        {topicsData.length > 0 ? (
          <ul>
            {topicsData.map((topic) => (
              <li key={topic.id}>
                <button
                  onClick={() => handleTopicSelect(topic.id)}
                  className={topic.id === selectedTopic ? 'active' : ''}
                 
                >
                  {topic.topic}
                </button>
                {topic.id === selectedTopic && topic.subtopics && (
                  <ul className="subtopics-list">
                    {topic.subtopics.map((subtopic) => (
                      <li key={subtopic.id}>
                        <button
                          onClick={() => handleSubtopicSelect(subtopic.subTopic)}
                          className={subtopic.subTopic === selectedSubtopic ? 'active' : ''}
                        >
                          {subtopic.subTopic}
                        </button>
                      </li>
                    ))}
                  </ul>
                )}
              </li>
            ))}
          </ul>
        ) : (
          <p>No topics available for the selected subject.</p>
        )}
      </div>

      {/* Right column: Content */}
      <div className="right-column">
        {selectedSubtopic ? (
          <>
            <h2>{selectedSubtopic}</h2>
            {content && content.length > 0 ? (
              <ul>
                {content.map((item, index) => (
                  <li key={index}>
                    <h3>{item.title}</h3>
                    <p>{item.description}</p>
                    <a href={item.link} target="_blank" rel="noopener noreferrer">
                      {item.link}
                    </a>
                    <p>Rating: {item.rating}/5</p>
                  </li>
                ))}
              </ul>
            ) : (
              <p>No content available for this subtopic.</p>
            )}
          </>
        ) : selectedTopic ? (
          <p>Select a subtopic to view its content.</p>
        ) : (
          <p>Select a topic to view its subtopics.</p>
        )}
      </div>
    </div>
  );
};

export default TwoColumnPage;
