import React, { useState, useEffect } from 'react';
import axios from 'axios';
// import '../../../../Css/RatingTooltip.css';

const Rating = ({ item, username }) => {
    const [showTooltip, setShowTooltip] = useState(false);
    const [feedbackMessage, setFeedbackMessage] = useState("");
    const [ratingDistribution, setRatingDistribution] = useState([0, 0, 0, 0, 0]); // Default counts
    const [totalRatings, setTotalRatings] = useState(0);
    const [averageRating, setAverageRating] = useState(0);

    // Fetch rating distribution and calculate overall rating
    // useEffect(() => {
    //     const fetchRatingDistribution = async () => {
    //         try {
    //             const response = await axios.get(`http://localhost:5000/api/rating/${item}`, {
    //                 params: { username }
    //             });
    //             const distribution = response.data;
    //             setRatingDistribution(distribution);

    //             // Calculate total ratings and average rating
    //             const total = distribution.reduce((acc, count) => acc + count, 0);
    //             const average = distribution.reduce(
    //                 (acc, count, index) => acc + count * (index + 1),
    //                 0
    //             ) / (total || 1);

    //             setTotalRatings(total);
    //             setAverageRating(average.toFixed(1)); // Round to 1 decimal place
    //         } catch (error) {
    //             console.error("Error fetching rating distribution:", error);
    //         }
    //     };

    //     fetchRatingDistribution();
    // }, [item, username]);

    const handleRatingSubmit = async (e, itemId) => {
        e.preventDefault();
        const ratingValue = e.target.ratingValue.value;

        try {
            const response = await axios.post('http://localhost:5000/api/rating', {
                itemId,
                username,
                rating: ratingValue,
            });

            if (response.status === 200) {
                setFeedbackMessage(`Thank you for rating item ${itemId} as ${ratingValue} stars!`);
            } else {
                setFeedbackMessage("Failed to submit rating. Please try again.");
            }
        } catch (error) {
            console.error("Error submitting rating:", error);
            if (error.response && error.response.status === 400) {
                setFeedbackMessage('You have already rated this item');
            } else {
                setFeedbackMessage('An error occurred. Please try again.');
            }
        }

        e.target.reset(); // Reset the form after submission
    };

    const renderBarGraph = () => {
        return (
            <div className="rating-bar-graph">
                {ratingDistribution.map((count, index) => {
                    const percentage = ((count / (totalRatings || 1)) * 100).toFixed(1);
                    return (
                        <div key={index} className="rating-bar-item">
                            <div className="rating-bar-label">
                                {5 - index} star
                            </div>
                            <div className="rating-bar">
                                <div
                                    className="rating-bar-fill"
                                    style={{ width: `${percentage}%` }}
                                ></div>
                            </div>
                            <div className="rating-bar-percentage">{percentage}%</div>
                        </div>
                    );
                })}
            </div>
        );
    };

    return (
        <div className="rating-component">
            {/* <h3 className="rating-overall">
                {averageRating} out of 5 ({totalRatings} ratings)
            </h3> */}
            {/* {renderBarGraph()} */}
            <form
                onSubmit={(e) => handleRatingSubmit(e, item)}
                className="rating-form"
            >
                <label htmlFor={`ratingValue-${item}`}>
                    Rate this item:
                </label>
                <input
                    type="number"
                    id={`ratingValue-${item}`}
                    name="ratingValue"
                    min="1"
                    max="5"
                    required
                    className="rating-input"
                />
                <button type="submit" className="rating-submit-btn">
                    Submit
                </button>
            </form>
            {feedbackMessage && <p className="rating-feedback">{feedbackMessage}</p>}
        </div>
    );
};

export default Rating;
