import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../../Css/Home.css'; // Import CSS for styling

const Home = ({username}) => {
    const [user, setUser] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [currentUsername, setCurrentUsername] = useState(username); // State for username

     // Effect to handle username changes
     useEffect(() => {
        setCurrentUsername(username); // Update the username state when prop changes
    }, [username]);

    // Fetch user details on component mount
    useEffect(() => {
        const fetchUserDetails = async () => {
            try {
                setUser(username);  
                console.log(user);
                const response = await axios.get(`http://localhost:5000/api/user?username=${user}`);
                setUsers(response.data.results); // Access the 'results' array from the response
            } catch (err) {
                setError('Error fetching user details'); // Handle error
            } finally {
                setLoading(false); // Set loading to false
            }
        };
        fetchUserDetails();
    }, [currentUsername]);

    // Render loading, error, or user details
    return (
        <div className="home-container">
            <header className="header">
                <h1>Welcome to the Student Information Portal</h1>
                <p>Empowering Students for Success</p>
            </header>
            <main className="student-profiles">
                {loading ? (
                    <p>Loading user details...</p>
                ) : error ? (
                    <p>{error}</p>
                ) : users && users.length > 0 ? ( 
                    users.map((user, index) => ( 
                        <div className="student-card" key={index}> 
                            <h1>Student Profile: {user.username}</h1>
                            <h3>{user.name}</h3>
                            <p><strong>Email:</strong> {user.email}</p>
                            
                        </div>
                    ))
                ) : (
                    <p>No user details available</p>
                )}
            </main>
            <footer className="footer">
                <p>Contact us: studentinfo@example.com | Phone: (123) 456-7890</p>
            </footer>
        </div>
    );
};

export default Home;
