import React from "react";
import Rating from './Function/Rating';

const NptelContent = () => {
  const nptelContent = [
    { id: 1, title: "NPTEL Course 1", description: "Introduction to Programming", link: "https://www.youtube.com/watch?v=xyz", rating: 4 },
    { id: 2, title: "NPTEL Course 2", description: "Advanced Data Structures", link: "https://www.youtube.com/watch?v=abc", rating: 5 },
    // { id: 3, title: "NPTEL Course 3", description: "Machine Learning Basics", link: "https://www.youtube.com/watch?v=def", rating: 3 },
  ];

  const truncateText = (text, maxLength) => {
    if (!text || text.length <= maxLength) return text;
    return text.substring(0, maxLength) + "...";
  };

  const renderCardContent = (item) => (
    <div key={item.id} className="card mb-3">
      <div className="card-body">
        <h3
          className="card-title"
          style={{
            whiteSpace: "nowrap",
            overflow: "hidden",
            textOverflow: "ellipsis",
          }}
        >
          {truncateText(item.title || "No Title Available", 50)}
        </h3>
        <p className="card-text">
          {truncateText(item.description || "No Description Available", 1000)}
        </p>
        <p>Rating: {"⭐".repeat(item.rating) || "N/A"}</p>
        {
          <img
            src="/image/image.png"
            alt="YouTube"
            className="youtube-logo"
            onClick={() => (item.link)}
          />
         }
        <Rating item={item.id} />
      </div>
    </div>
  );
  return (
    <div>
      {nptelContent.map(renderCardContent)}
    </div>
  );
};

export default NptelContent;
