import React from 'react';
import './ToolTips.css'; // Optional: for styling

const ToolTip = ({ description }) => {
  const truncateText = (text, maxLength) => {
    if (!text || text.trim().length === 0) return "No description available"; // Default message
    if (text.length <= maxLength) return text; // Return original text if it's short enough
    return text.substring(0, maxLength) + "..."; // Truncate and add ellipsis
  };

  return (
    <div className="tooltip">
      <p>{truncateText(description, 20)}</p>
    </div>
  );
};

export default ToolTip;
