import React from "react";
import "./Table.css";
import "./functionality/ToolTip.css";
import ToolTip from "./functionality/ToolTip";

const ChapterContent = ({ subTopicData }) => {
  // Extract levels and other details from subTopicData
  const { title, levels } = subTopicData;

  return (
    <div className="chapter-content">
      <h2>{title || "Untitled Subtopic"}</h2>
      
      {/* Dynamically rendering each level */}
      {Object.keys(levels).map((level) => {
        // Extract level data dynamically
        const levelData = levels[level];

        return levelData && levelData.length > 0 ? (
          <div key={level} className="content-level">
            <h4>{level.charAt(0).toUpperCase() + level.slice(1)} Level</h4>
            <ul>
              {levelData.map((item, index) => (
                <li key={index} className="content-item">
                  {/* Render item details */}
                  <h5>{item.title || "No Title Available"}</h5>
                  <p>{item.description || "No Description Available"}</p>
                  {item.link && (
                    <a href={item.link} target="_blank" rel="noopener noreferrer">
                      Read More
                    </a>
                  )}
                  <p>Rating: {item.rating || "N/A"}/5</p>
                </li>
              ))}
            </ul>
          </div>
        ) : null;
      })}
    </div>
  );
};

export default ChapterContent;
