import React, { useState, useEffect } from "react";
import axios from "axios";

const DropDown = ({ subjectId }) => {
  const [topics, setTopics] = useState([]);
  const [subtopics, setSubtopics] = useState([]);
  const [links, setLinks] = useState([]);
  const [selectedTopic, setSelectedTopic] = useState(null);
  const [selectedSubTopic, setSelectedSubTopic] = useState(null);

  // Fetch topics when the component mounts or subjectId changes
  useEffect(() => {
    const fetchTopics = async () => {
      try {
        const response = await axios.get(`/api/topics?subjectId=${subjectId}`);
        setTopics(response.data);
      } catch (error) {
        console.error("Error fetching topics:", error);
      }
    };
    fetchTopics();
  }, [subjectId]);

  // Fetch subtopics when a topic is selected
  useEffect(() => {
    if (selectedTopic) {
      const fetchSubtopics = async () => {
        try {
          const response = await axios.get(`/api/subtopics?topicId=${selectedTopic}`);
          setSubtopics(response.data);
        } catch (error) {
          console.error("Error fetching subtopics:", error);
        }
      };
      fetchSubtopics();
    }
  }, [selectedTopic]);

  // Fetch links when a subtopic is selected
  useEffect(() => {
    if (selectedSubTopic) {
      const fetchLinks = async () => {
        try {
          const response = await axios.get(`/api/links?subTopicId=${selectedSubTopic}`);
          setLinks(response.data);
        } catch (error) {
          console.error("Error fetching links:", error);
        }
      };
      fetchLinks();
    }
  }, [selectedSubTopic]);

  return (
    <div className="container mt-4">
      <div className="row">
        {/* Left Column: Topics and Subtopics */}
        <div className="col-md-6">
          <h3>Topics</h3>
          <ul className="list-group">
            {topics.map((topic) => (
              <li
                key={topic.id}
                className="list-group-item"
                onClick={() => setSelectedTopic(topic.id)}
              >
                {topic.topic}
              </li>
            ))}
          </ul>
          {selectedTopic && (
            <div>
              <h4>Subtopics</h4>
              <ul className="list-group">
                {subtopics.map((subtopic) => (
                  <li
                    key={subtopic.id}
                    className="list-group-item"
                    onClick={() => setSelectedSubTopic(subtopic.id)}
                  >
                    {subtopic.subTopic}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>

        {/* Right Column: Links (Content) */}
        <div className="col-md-6">
          {selectedSubTopic && (
            <div>
              <h3>Content</h3>
              <ul className="list-group">
                {links.map((link, index) => (
                  <li key={index} className="list-group-item">
                    <h5>{link.title}</h5>
                    <a href={link.link} target="_blank" rel="noopener noreferrer">
                      {link.link}
                    </a>
                    <p>{link.description}</p>
                    <p>Rating: {link.rating}</p>
                    <p>Level: {link.level}</p>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default DropDown;
