import React, { useState, useEffect } from "react";
import Link from "./Link"; // Ensure Link component is correctly implemented
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { useLocation } from "react-router-dom";
// import NptelContent from "./NptelContent";

const AddTopic = () => {
  const location = useLocation();
  const { course, branch, semester, subject } = location.state || {};
  const [subTopic, setSubTopic] = useState("");
  const [levels, setLevels] = useState({
    basic: [],
    medium: [],
    advanced: [],
  });
  const [topics, setTopics] = useState([]);
  const [selectedTopic, setSelectedTopic] = useState("");
  const [newTopic, setNewTopic] = useState("");
  const [isAddingNewTopic, setIsAddingNewTopic] = useState(false);
  const [loading, setLoading] = useState(false); // Loading state for fetching topics
  const [nptelContents, setNptelContents] = useState([]); // State for multiple NPTEL contents

  const navigate = useNavigate();

  // Fetch topics from the database when the component mounts or `subject` changes
  useEffect(() => {
    const fetchTopics = async () => {
      setLoading(true); // Start loading
      try {
        if (subject) {
          const response = await axios.get(
            `http://localhost:5000/api/topics?subjectId=${subject}`
          );
          setTopics(response.data);
        }
      } catch (error) {
        console.error("Error fetching topics:", error);
      } finally {
        setLoading(false); // Stop loading
      }
    };
    fetchTopics();
  }, [subject]);

  const addLink = (level) => {
    setLevels((prev) => ({
      ...prev,
      [level]: [
        ...prev[level],
        { title: "", link: "", description: "", rating: 0 },
      ],
    }));
  };

  const removeLink = (level, index) => {
    setLevels((prev) => ({
      ...prev,
      [level]: prev[level].filter((_, i) => i !== index),
    }));
  };

  const handleInputChange = (level, index, field, value) => {
    setLevels((prev) => ({
      ...prev,
      [level]: prev[level].map((item, i) =>
        i === index ? { ...item, [field]: value } : item
      ),
    }));
  };

  const handleNptelContentChange = (index, data) => {
    setNptelContents((prev) =>
      prev.map((content, i) => (i === index ? data : content))
    );
  };

  const addNptelContent = () => {
    setNptelContents((prev) => [
      ...prev,
      { video_title: "", video_description: "", video_name: "" },
    ]);
  };

  const removeNptelContent = (index) => {
    setNptelContents((prev) => prev.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate inputs
    if (!selectedTopic && !newTopic) {
      alert("Please select a topic or enter a new topic.");
      return;
    }
    if (!subTopic) {
      alert("Please enter a subtopic.");
      return;
    }

    const requestData = {
      subTopic,
      selectedTopic: isAddingNewTopic ? newTopic : selectedTopic,
      isAddingNewTopic,
      courseId: course,
      branchId: branch,
      semesterId: semester,
      subjectId: subject,
      levels,
      nptelContents, // Include NPTEL content in request data
    };

    try {
      const response = await axios.post(
        "http://localhost:5000/api/topics/add-topic",
        requestData
      );
      console.log("Topic and links added successfully:", response.data);

      // Navigate back to the previous page after successful submission
      navigate(-1);
    } catch (error) {
      console.error("Error adding topic:", error);
      alert("An error occurred while adding the topic. Please try again.");
    }
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">Add Topic</h2>
      {loading ? (
        <p>Loading topics...</p> // Show loading message while fetching topics
      ) : (
        <form onSubmit={handleSubmit}>
          {/* Dropdown for selecting a topic */}
          <div className="mb-3">
            <label htmlFor="topic" className="form-label">
              Select Topic
            </label>
            <select
              id="topic"
              className="form-select"
              value={selectedTopic}
              onChange={(e) => {
                const value = e.target.value;
                setSelectedTopic(value);
                setIsAddingNewTopic(value === "add_new_topic");
              }}
            >
              <option value="">-- Select a Topic --</option>
              {topics.map((topic) => (
                <option key={topic.id} value={topic.topic}>
                  {topic.topic}
                </option>
              ))}
              <option value="add_new_topic">Add New Topic</option>
            </select>
          </div>

          {/* Show input field for new topic if "Add New Topic" is selected */}
          {isAddingNewTopic && (
            <div className="mb-3">
              <label htmlFor="newTopic" className="form-label">
                New Topic Name
              </label>
              <input
                type="text"
                id="newTopic"
                className="form-control"
                value={newTopic}
                onChange={(e) => setNewTopic(e.target.value)}
              />
            </div>
          )}

          <div className="mb-3">
            <label htmlFor="subTopic" className="form-label">
              Subtopic Name
            </label>
            <input
              type="text"
              id="subTopic"
              className="form-control"
              value={subTopic}
              onChange={(e) => setSubTopic(e.target.value)}
            />
          </div>

          {["basic", "medium", "advanced"].map((level) => (
            <div key={level} className="mb-4">
              <h4 className="text-capitalize">{level} Level</h4>
              {levels[level].map((item, index) => (
                <Link
                  key={`${level}-${index}`}
                  level={level}
                  index={index}
                  data={item}
                  handleInputChange={handleInputChange}
                  removeLink={removeLink}
                />
              ))}
              <button
                type="button"
                className="btn btn-primary"
                onClick={() => addLink(level)}
              >
                Add {level.charAt(0).toUpperCase() + level.slice(1)} Link
              </button>
            </div>
          ))}

          <h4 className="mt-4">NPTEL Content</h4>
          {nptelContents.map((content, index) => (
            <div key={index} className="mb-3">
              <NptelContent
                data={content}
                onChange={(data) => handleNptelContentChange(index, data)}
              />
              <button
                type="button"
                className="btn btn-danger mt-2"
                onClick={() => removeNptelContent(index)}
              >
                Remove NPTEL Content
              </button>
            </div>
          ))}

          <button
            type="button"
            className="btn btn-secondary mb-4"
            onClick={addNptelContent}
          >
            Add NPTEL Content
          </button>

          <button type="submit" className="btn btn-success">
            Save Topic
          </button>
        </form>
      )}
    </div>
  );
};

export default AddTopic;
