import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../../../Css/TwoColumnPage.css';

const TwoColumnPage = ({ selectedSubject }) => {
  const [topicsData, setTopicsData] = useState([]);
  const [selectedTopic, setSelectedTopic] = useState(null);
  const [selectedSubtopic, setSelectedSubtopic] = useState(null);
  const [content, setContent] = useState([]);

  // Fetch topics and subtopics
  useEffect(() => {
    const fetchTopics = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/topics/${selectedSubject}`);
        setTopicsData(response.data.topics);
      } catch (error) {
        console.error('Error fetching topics:', error);
      }
    };

    if (selectedSubject) fetchTopics();
  }, [selectedSubject]);

  // Handle topic selection
  const handleTopicSelect = (topicId) => {
    setSelectedTopic(topicId);
    setSelectedSubtopic(null);
    setContent([]);
  };

  // Handle subtopic selection
  const handleSubtopicSelect = (subtopicName) => {
    setSelectedSubtopic(subtopicName);
    const topic = topicsData.find((t) => t.topicId === selectedTopic);
    if (topic) {
      setContent(topic.links.filter((link) => link.level === subtopicName));
    }
  };

  return (
    <div className="two-column-page">
      {/* Left Column */}
      <div className="left-column">
        <h3>Topics</h3>
        <ul>
          {topicsData.map((topic) => (
            <li key={topic.topicId}>
              <button
                onClick={() => handleTopicSelect(topic.topicId)}
                className={topic.topicId === selectedTopic ? 'active' : ''}
              >
                {topic.topicName}
              </button>
              {selectedTopic === topic.topicId && (
                <ul className="subtopics-list">
                  <li>
                    <button
                      onClick={() => handleSubtopicSelect('basic')}
                      className={selectedSubtopic === 'basic' ? 'active' : ''}
                    >
                      Basic
                    </button>
                  </li>
                  <li>
                    <button
                      onClick={() => handleSubtopicSelect('medium')}
                      className={selectedSubtopic === 'medium' ? 'active' : ''}
                    >
                      Medium
                    </button>
                  </li>
                  <li>
                    <button
                      onClick={() => handleSubtopicSelect('advanced')}
                      className={selectedSubtopic === 'advanced' ? 'active' : ''}
                    >
                      Advanced
                    </button>
                  </li>
                </ul>
              )}
            </li>
          ))}
        </ul>
      </div>

      {/* Right Column */}
      <div className="right-column">
        {selectedSubtopic ? (
          <div>
            <h2>{selectedSubtopic} Content</h2>
            <ul>
              {content.map((item, index) => (
                <li key={index}>
                  <h3>{item.title}</h3>
                  <p>{item.description}</p>
                  <a href={item.link} target="_blank" rel="noopener noreferrer">
                    {item.link}
                  </a>
                  <p>Rating: {item.rating}/5</p>
                </li>
              ))}
            </ul>
          </div>
        ) : selectedTopic ? (
          <p>Select a subtopic to view content.</p>
        ) : (
          <p>Select a topic to view subtopics.</p>
        )}
      </div>
    </div>
  );
};

export default TwoColumnPage;
