import React from "react";
import "./Table.css";
import "./functionality/ToolTip.css";
import ToolTip from "./functionality/ToolTip";

const ChapterContent = ({ selectedSubject, selectedSubtopic, data, selectedTopic }) => {
  const topicData = data.topics[selectedSubtopic]; // Get data for the selected subtopic

  if (!topicData) {
    return <p>No data available for the selected subtopic.</p>;
  }

  const { Basic, Intermediate, Advanced, NPTEL, LiveVideo } = topicData;

  return (
    <div className="containers">
      <h2 className="subject-title">Subject: {selectedSubject}</h2>
      <div className="chapter">
        <div className="topic">
          <p>Topic: {selectedTopic}</p>
          <p>Subtopic: {selectedSubtopic}</p>
          <table className="content-table">
            <thead>
              <tr>
                <th>Basic Level</th>
                <th>Intermediate Level</th>
                <th>Advanced Level</th>
                <th>Local NPTEL</th>
                <th>Live Video</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                {/* Basic Level */}
                <td>
                  {Basic.map((item, index) => (
                    <div key={index} className="tooltip-container">
                      <ToolTip content={item} item={item.title} />
                    </div>
                  ))}
                </td>

                {/* Intermediate Level */}
                <td>
                  {Intermediate.map((item, index) => (
                    <div key={index} className="tooltip-container">
                      <ToolTip content={item} item={item} />
                    </div>
                  ))}
                </td>

                {/* Advanced Level */}
                <td>
                  {Advanced.map((item, index) => (
                    <div key={index} className="tooltip-container">
                      <ToolTip content={item} item={item} />
                    </div>
                  ))}
                </td>

                {/* Local NPTEL */}
                <td>
                  {NPTEL.map((item, index) => (
                    <div key={index} className="tooltip-container">
                      <ToolTip content={item} item={item} />
                      <div className="rating">{"⭐".repeat(item.rating || 0)}</div>
                    </div>
                  ))}
                </td>

                {/* Live Video */}
                <td>
                  {LiveVideo.map((item, index) => (
                    <div key={index} className="tooltip-container">
                      <ToolTip content={item} item={item} />
                      <div className="rating">{"⭐".repeat(item.rating || 0)}</div>
                    </div>
                  ))}
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default ChapterContent;
