import React, { useState, useEffect } from "react";
import axios from "axios";
import "./NptelComponent.css"; // Optional CSS for styling

const NptelComponent = ({ onVideoSelect }) => {
  const [nptelContent, setNptelContent] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch NPTEL content on component mount
    axios
      .get("http://192.168.68.12:9080/Tv")
      .then((response) => {
        setNptelContent(response.data);
      })
      .catch((error) => {
        console.error("Error fetching NPTEL content:", error);
        setError("Failed to fetch NPTEL content.");
      });
  }, []);

  if (error) {
    return <p className="error-message">{error}</p>;
  }

  return (
    <div className="nptel-container">
      {nptelContent.length > 0 ? (
        nptelContent.map((channel, index) => (
          <div key={index} className="nptel-channel">
            <p>{channel.title}</p>
            <button
              className="btn btn-primary btn-sm"
              onClick={() => onVideoSelect(channel.link)}
            >
              Watch Live
            </button>
          </div>
        ))
      ) : (
        <p>Loading NPTEL Content...</p>
      )}
    </div>
  );
};

export default NptelComponent;
